import {Person} from '../@types/common';
import * as XLSX from "xlsx";

export function getCompany(){
    const company = [
        {
            code : '01',
            name : '마이체크업'
        },
        {
            code : '02',
            name : '벨류맵'
        }
    ];

    return company;
}
export function getCode(type : string){
    const company = [
        {
            type : '01',
            code : '1',
            name : '마이체크업'
        },
        {
            type : '02',
            code : '02',
            name : '벨류맵'
        }
    ];

    for(let idx of company){
        if(idx.type === type) return idx.code;
    }
    return '00';
}

export function excelDownload(type: string, data : Person[]){
    const excelHandler = {
        getExcelFileName: () => {
            return "유저 데이터.xlsx";
        },
        getSheetName: ()  => {
            return type;
        },
        getExcelData: () => {
            return data;
        },
        getWorksheet: () => {
            return XLSX.utils.json_to_sheet(excelHandler.getExcelData());
        },
    };

    const datas = excelHandler.getWorksheet();
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, datas, excelHandler.getSheetName());
    XLSX.writeFile(workbook, excelHandler.getExcelFileName());
}
