import React from "react";
import Slider from "react-slick";
import Left from '../assets/images/left.svg';
import Right from '../assets/images/right.svg';
import {Person} from '../@types/common';

function SampleNextArrow(props : any) {
    const {  onClick } = props;
    return (
        <button
            className='customNext'
            onClick={onClick}
        >
            <img src={Right} alt={'left'} width={20}/>
        </button>
    );
}

function SamplePrevArrow(props : any) {
    const { onClick } = props;
    return (
        <button
            className='customPrev'
            onClick={onClick}
        >
            <img src={Left} alt={'left'} width={20}/>
        </button>
    );
}

interface Props {
    data : Person[]
}
function CustomSlider({data} : Props) {


    const settings = {
        dots: true,
        infinite: true,
        vertical: true,
        verticalSwiping: true,
        slidesToShow: 4,
        slidesToScroll: 1,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />
    };
    return (
        <div className="h-full">
                {
                    data.length > 0 ?
                        <Slider {...settings}>
                            {
                                data.map((idx, index) =>
                                        <div className='w-[200px] h-[100px] bg-white border rounded-xl mb-8 shadow px-4 py-4 flex flex-col' key={index}>
                                            <table className="table-auto w-full">
                                                <thead>
                                                <tr>
                                                    <th>
                                                        No.
                                                    </th>
                                                    <th>
                                                        고객키
                                                    </th>
                                                    <th>
                                                        신청자
                                                    </th>
                                                    <th>
                                                        핸드폰
                                                    </th>
                                                    <th>
                                                        생년월일
                                                    </th>
                                                    <th>
                                                        나이(성별)
                                                    </th>
                                                </tr>
                                                </thead>
                                                <tbody>
                                                <tr>
                                                    <td>
                                                        {index + 1}
                                                    </td>
                                                    <td>
                                                        {idx.cmpk}
                                                    </td>
                                                    <td>
                                                        {idx.cName}
                                                    </td>
                                                    <td>
                                                        {idx.cCell}
                                                    </td>
                                                    <td>
                                                        {idx.cJumin}
                                                    </td>
                                                    <td>
                                                        {idx.age}
                                                    </td>
                                                </tr>
                                                </tbody>
                                            </table>
                                        </div>
                                )
                            }
                        </Slider>

                        :
                        <div className='w-full flex_center'>
                            없음
                        </div>
                }
        </div>
    );
}

export default CustomSlider;
