import {Person} from '../@types/common';
import * as XLSX from "xlsx-js-style";
import dayjs from 'dayjs';
import getDate from "../utils/getDate";

export function getCompany(){
    const company = [
        {
            code : '01',
            name : '마이체크업'
        },
        {
            code : '02',
            name : '벨류맵'
        }
    ];

    return company;
}
export function getCode(type : string){
    const company = [
        {
            type : '01',
            code : '1',
            name : '마이체크업'
        },
        {
            type : '02',
            code : '02',
            name : '벨류맵'
        }
    ];

    for(let idx of company){
        if(idx.type === type) return idx.code;
    }
    return '00';
}

export function excelDownload(type: string, data: Person[]): void {
    let today = dayjs().format('YYYY-MM-DD');
    let excel: Excel[] | undefined = excelData(type, data); // 타입 지정
    if (!excel) {
        console.error('유효한 타입이 아닙니다.');
        return;
    }

    const excelHandler = {
        getExcelFileName: () => {
            return "유저 데이터.xlsx";
        },
        getSheetName: () => {
            return today + '_' + type;
        },
        getExcelData: () => {
            return excel;
        },
        getWorksheet: () => {
            const ws = XLSX.utils.json_to_sheet(excelHandler.getExcelData() || []); // undefined를 빈 배열로 처리

            // 특정 셀에 스타일 적용 예제
            ws['$A1:E1'].s = {
                alignment: { horizontal: 'center' },
                font: { bold: true, color: { rgb: 'FFFFFF' } }, // 폰트 색상을 빨간색으로 지정
                fill: { bgColor: { indexed: 64 }, fgColor: { rgb: '006ED0' } }, // 배경색을 노란색으로 지정
            };
            /*ws['B1'].s = {
                alignment: { horizontal: 'center' } ,
                font: { bold: true, color: { rgb: 'FFFFFF' } }, // 폰트 색상을 빨간색으로 지정
                fill: { bgColor: { indexed: 64 }, fgColor: { rgb: '006ED0' } }, // 배경색을 노란색으로 지정
            };
            ws['C1'].s = {
                alignment: { horizontal: 'center' },
                font: { bold: true, color: { rgb: 'FFFFFF' } }, // 폰트 색상을 빨간색으로 지정
                fill: { bgColor: { indexed: 64 }, fgColor: { rgb: '006ED0' } }, // 배경색을 노란색으로 지정
            };
            ws['D1'].s = {
                alignment: { horizontal: 'center' },
                font: { bold: true, color: { rgb: 'FFFFFF' } }, // 폰트 색상을 빨간색으로 지정
                fill: { bgColor: { indexed: 64 }, fgColor: { rgb: '006ED0' } }, // 배경색을 노란색으로 지정
            };
            ws['E1'].s = {
                alignment: { horizontal: 'center' },
                font: { bold: true, color: { rgb: 'FFFFFF' } }, // 폰트 색상을 빨간색으로 지정
                fill: { bgColor: { indexed: 64 }, fgColor: { rgb: '006ED0' } }, // 배경색을 노란색으로 지정
            };*/
            ws['!cols'] = [
                {
                    wpx: 120,
                },
                {
                    wpx: 80,
                },
                {
                    wpx: 120,
                },
                {
                    wpx: 120,
                },
                {
                    wpx: 80,
                },
            ];

            return ws;
        },
    };

    const datas = excelHandler.getWorksheet();
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, datas, excelHandler.getSheetName());
    XLSX.writeFile(workbook, excelHandler.getExcelFileName());
}


interface Excel {
    고객키 : string,
    이름: string,
    생년월일: string,
    연락처: string,
    나이: string
}

function excelData(type: string, data: any[]): Excel[] | undefined {
    console.log(type);
    if (type === '벨류맵') {
        return data.map((item) => ({
            고객키: item.cmpk,
            이름: item.cName,
            생년월일: item.cJumin,
            연락처: item.cCell,
            나이: item.age
        }));
    }
    return undefined;
}
