import React from "react";
import Slider from "react-slick";
import Left from '../assets/images/left.svg';
import Right from '../assets/images/right.svg';
import {Person} from '../@types/common';

function SampleNextArrow(props : any) {
    const {  onClick } = props;
    return (
        <button
            className='customNext'
            onClick={onClick}
        >
            <img src={Right} alt={'left'} width={20}/>
        </button>
    );
}

function SamplePrevArrow(props : any) {
    const { onClick } = props;
    return (
        <button
            className='customPrev'
            onClick={onClick}
        >
            <img src={Left} alt={'left'} width={20}/>
        </button>
    );
}

interface Props {
    data : Person[]
}
function CustomSlider({data} : Props) {


    const settings = {
        dots: true,
        infinite: true,
        vertical: true,
        verticalSwiping: true,
        slidesToShow: 4,
        slidesToScroll: 1,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />
    };
    return (
        <div className="h-full">
                {
                    data.length > 0 ?
                    data.map((idx, index) =>
                        <Slider {...settings}>
                            <div className='w-[200px] h-[100px] bg-white border rounded-xl mb-8 shadow' key={index}>
                                <h3>{idx.cName}</h3>
                            </div>
                        </Slider>
                    )
                        :
                        <div>없음</div>
                }
        </div>
    );
}

export default CustomSlider;
