import Menu from '../assets/images/menu.svg';
import React, { useState } from "react";
import cn from "classnames";
import Left from '../assets/images/angles-left-solid.svg';
import {getCompany} from '../utils/common';
export default function NavBar() {
    const [open, setOpen] = useState(false);

    return (
        <div className={cn('fixed top-0 left-0  h-full bg-white flex flex-col py-8 z-[9999] transition-all duration-[900ms] ease-in-out', {'w-[140px]': !open, 'w-[300px]': open})}>
            <div className='flex_center'>
                <button
                    className={cn('w-12 h-12 flex_center transition duration-150 ease-in-out hover:w-[150px]', {'animate-[nav-sheet-right_800ms_ease-in-out]': open}, {'animate-[nav-sheet-left_800ms_ease-in-out]': !open})}
                    onClick={(event: React.MouseEvent<HTMLButtonElement>) => setOpen(!open)}>
                    {open ?
                        <img src={Left} alt={'Left'} width={40}/>
                        :
                        <img src={Menu} alt={'menu'} width={40}/>
                    }
                </button>
            </div>
            {
                open &&
                <div className='flex_center flex-col mt-32 text-[16px] font-normal'>
                    {
                        getCompany().map((idx, index) =>
                            <div className='mb-8' key={index}>
                            <a >
                                    {idx.name}
                                </a>
                            </div>
                        )
                    }
                </div>
            }
        </div>
    )
}
