import Menu from '../assets/images/menu.svg';
import React, { useState } from "react";
import cn from "classnames";
import Left from '../assets/images/angles-left-solid.svg';

export default function NavBar() {
    const [open, setOpen] = useState(false);

    return (
        <div className={cn('fixed top-0 left-0 w-[120px] h-full bg-white flex flex-col py-8 z-[9999]', {'w-[200px]': open})}>
            <div className='flex_center'>
                <button className={cn('w-[60px] h-[60px] flex_center', {'animate-[nav-sheet-right_800ms_ease-in-out]' : open }, {'animate-[nav-sheet-left_800ms_ease-in-out]' : !open})} onClick={(event : React.MouseEvent<HTMLButtonElement>) => setOpen(!open)}>
                    {open ?
                        <img src={Left} alt={'Left'} width={40}/>
                        :
                        <img src={Menu} alt={'menu'} width={40}/>
                    }
                </button>
            </div>
        </div>
    )
}
