import {Person} from '../@types/common';
import * as XLSX from "xlsx";
import dayjs from 'dayjs';
import getDate from "../utils/getDate";

export function getCompany(){
    const company = [
        {
            code : '01',
            name : '마이체크업'
        },
        {
            code : '02',
            name : '벨류맵'
        }
    ];

    return company;
}
export function getCode(type : string){
    const company = [
        {
            type : '01',
            code : '1',
            name : '마이체크업'
        },
        {
            type : '02',
            code : '02',
            name : '벨류맵'
        }
    ];

    for(let idx of company){
        if(idx.type === type) return idx.code;
    }
    return '00';
}

export function excelDownload(type: string, data : Person[]){
    let today = dayjs().format('YYYY-MM-DD');
    const excelHandler = {
        getExcelFileName: () => {
            return "유저 데이터.xlsx";
        },
        getSheetName: ()  => {
            return today+'_'+type;
        },
        getExcelData: () => {
            return data ;
        },
        getWorksheet: () => {
            const ws = XLSX.utils.json_to_sheet(excelHandler.getExcelData());

            ws['A1'].s = {
                font: { bold: true, color: { rgb: 'FF0000' } }, // 폰트 색상을 빨간색으로 지정
                fill: { bgColor: { indexed: 64 }, fgColor: { rgb: 'FFFF00' } }, // 배경색을 노란색으로 지정
                border: { top: { style: 'thin', color: { rgb: '000000' } }, bottom: { style: 'thin', color: { rgb: '000000' } } } // 테두리 스타일 지정
            };

            return ws;
        },
    };

    const datas = excelHandler.getWorksheet();
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, datas, excelHandler.getSheetName());
    XLSX.writeFile(workbook, excelHandler.getExcelFileName());
}
