import React, { useState } from "react";
import Logo from '../assets/images/top_logo.png';
import cn from "classnames";
import { Link } from 'react-router-dom';
import { useMediaQuery } from "react-responsive";
import Menu from '../assets/images/menu.svg';



export default function Header() {
    const isPc = useMediaQuery({ query: "(min-width: 990px)" });
    const [isOpen, setIsOpen] = useState(false);
    console.log(isPc);

    return (
        <div className='w-full border-b h-[80px]'>
            <div className='header'>
                <Link to='/'>
                    <h2 className='font-[500] text-[#FFD840] text-[24px]'>KMPARK</h2>
                </Link>
                {isPc ? (
                    <div className='ml-auto'>
                        <ul className='flex space-x-8'>
                            <li className='li_hover'>장애접수</li>
                            <li className='li_hover'>관리</li>
                        </ul>
                    </div>
                ) : (
                    <div className='ml-auto'>
                        <div className='w-[45px] h-[40px] border bg_base rounded-md'>
                            <button className='w-full h-full flex_center' onClick={() => setIsOpen(!isOpen)}>
                                <img src={Menu} alt='menu' width={40} />
                            </button>
                        </div>
                    </div>
                )}
            </div>
            <div className={cn('absolute nav_bar w-full text-white flex_center flex-col text-[20px] transition-all duration-500 ease-in-out', {
                'h-[0px]': !isOpen,
                'h-[120px]': isOpen
            })}>
                <div className='hover:font-bold hover:text-[#FFD840] cursor-pointer w-full h-[60px]'>
                    <button className='w-full h-full'>장애접수</button>
                </div>
                <div className='hover:font-bold hover:text-[#FFD840] cursor-pointer w-full h-[60px]'>
                    <button className='w-full h-full'>관리</button>
                </div>
            </div>
        </div>
    )
}
