import {useMediaQuery} from "react-responsive";
import cn from 'classnames';

export default function Footer(){
    const isPc = useMediaQuery({ query: "(min-width: 990px)" });

    return (
        <div className={cn('w-full border-t h-[140px]',{'fixed bottom-0 left-0' : isPc})}>
            <div className='mx-auto my-0 h-full flex items-center w-[calc(100%-80px)] max-w-[1280px] text-[12px]'>
                <div className='flex w-full'>
                    <div>
                        <p className='font-bold text-black'>(주)케이엠파크</p>
                        <p className='mt-2'>사업자 등록번호 : 789-81-01455 · 대표이사 : 유은진 · 서울특별시 강남구 자곡로 강남에이스 타워 215</p>
                    </div>
                    <div className='ml-auto pr-8 flex flex-col'>
                        <p className='font-bold text-black'>고객센터 070-7841-5964</p>
                        <p><span className='font-bold text-black mr-2'>전화상담</span> 평일 9시 ~ 18시, 주말 · 공휴일 휴무</p>
                        <p className='font-bold text-black mt-4'><span className='mr-2'>이메일 문의</span> sj@simg.kr</p>
                    </div>
                </div>
            </div>
        </div>
    )
}
