import GetLocation from "./getLocation";
import React, {useState} from "react";
import axiosInstance from '../api/axiosInstans';
import {Location} from '../@types/common';
import cn from 'classnames';
import {useMediaQuery} from "react-responsive";

export default function InsuRequest(){
    const [data, setData] = useState<Location[]>([]);
    const [parkingName, setParkinName] = useState('');
    const isPc = useMediaQuery({ query: "(min-width: 990px)" });
    function isScrollable() {
        // 현재 문서의 높이
        const documentHeight = document.documentElement.scrollHeight;
        // 창의 높이
        const windowHeight = window.innerHeight;

        // 현재 문서의 높이가 창의 높이보다 크면 스크롤이 생겼음을 의미
        return documentHeight > windowHeight;
    }

// 스크롤이 있는지 여부를 확인하여 결과를 콘솔에 출력
    console.log(isScrollable());
    const onClickSearch = (e : React.MouseEvent) => {
        const fetchData = async () => {
            try {
                let data = {
                    parkingName : parkingName,
                    sort :'name'
                }
                const result: any = await axiosInstance.post('/parkingNameSerch',data);
                setData(result.data);
                if(result.length <= 0 ){
                    alert("데이터가 없습니다.")
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }
    return(
        <div className={cn('w-full pt-[120px]', {'h-[650px]': isPc})}>
                <div className='div_wrap flex_center'>
                    <div className='my-8 w-full flex_center flex-col'>
                        <div className={cn('flex items-center w-[450px] border h-[50px]',{'w-[450px] text-[16px]' : isPc, 'w-[360px] text-[14px]' : !isPc})}>
                            <div
                                className=' text-black font-[500] h-full flex_center border-r basis-1/3'>
                                주차장명
                            </div>
                            <input type='text' className='search_input' onChange={(e:React.ChangeEvent<HTMLInputElement>) => setParkinName(e.currentTarget.value)}/>
                            <div
                                className=' text-black  font-[500] h-full flex_center border-r basis-1/3'>
                                <button className='w-full h-full text-white bg_base hover:text-[#FFD840]' onClick={onClickSearch}>
                                    SEARCH
                                </button>
                            </div>
                        </div>
                        <GetLocation data={data}/>
                    </div>
                </div>
        </div>
    )
}
