import cn from 'classnames';
import {useMediaQuery} from "react-responsive";
import React, {useEffect, useState} from "react";
import Login from "./login";
import GetLocation from "./getLocation";
import axiosInstance from "../api/axiosInstans";
import {Location} from "../@types/common";

export default function Admin(){
    const isPc = useMediaQuery({ query: "(min-width: 990px)" });
    const [scrolled, setScrolled] = useState(false);
    const [parkingName, setParkinName] = useState('');
    const [data, setData] = useState<Location[]>([]);
    const [login, setLogin] = useState(false);
    function isScrollable() {
        // 현재 문서의 높이
        const documentHeight = document.documentElement.scrollHeight;
        // 창의 높이
        const windowHeight = window.innerHeight;

        // 현재 문서의 높이가 창의 높이보다 크면 스크롤이 생겼음을 의미
        return documentHeight > windowHeight;
    }

    const onClickSearch = (e : React.MouseEvent) => {
        const fetchData = async () => {
            try {
                let data = {
                    parkingName : parkingName,
                    sort :'name'
                }
                const result: any = await axiosInstance.post('/parkingNameSerch',data);
                setData(result.data);
                if(result.length <= 0 ){
                    alert("데이터가 없습니다.")
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }

    useEffect(() => {
        setScrolled(isScrollable);
    }, []);
    useEffect(() => {
        if(sessionStorage.getItem('upk')){
            setLogin(true);
        }
    }, [login]);
    return(
        <div className={cn('w-full pt-[120px]', {'h-[400px]': !scrolled, 'pb-[140px]' : scrolled})}>
            <div className='div_wrap flex_center'>
                {
                    login ?
                        <div className='my-8 w-full flex_center flex-col'>
                            <div className={cn('flex items-center  border h-[50px]', {
                                'w-[450px] text-[16px]': isPc,
                                'w-[360px] text-[12px]': !isPc
                            })}>
                                <div
                                    className=' text-black font-[500] h-full flex_center border-r basis-1/3'>
                                    주차장명
                                </div>
                                <input type='text' className='search_input'
                                       onChange={(e: React.ChangeEvent<HTMLInputElement>) => setParkinName(e.currentTarget.value)}/>
                                <div
                                    className=' text-black  font-[500] h-full flex_center border-r basis-1/3'>
                                    <button className='w-full h-full text-white bg_base hover:text-[#FFD840]'
                                            onClick={onClickSearch}>
                                        SEARCH
                                    </button>
                                </div>
                            </div>
                            <GetLocation data={data}/>
                        </div>
                        :
                        <Login setLogin={setLogin}/>
                }
            </div>
        </div>
    )
}
