import React, {SetStateAction, useState} from "react";
import Logo from '../assets/images/top_logo.png';
import cn from "classnames";
import { Link } from 'react-router-dom';
import {useMediaQuery} from "react-responsive";
import Menu from '../assets/images/menu.svg';

interface Props {
    type?: string,
    comp?: string,
    setType : React.Dispatch<SetStateAction<string>>,
}
export default function Header() {
    const isPc = useMediaQuery({query : "(min-width : 990px"});
    const [isOpen,setIsOpen] = useState(false);
    console.log(isPc);
    return (
        <div className='w-full border-b h-[80px]'>
            <div className='header'>
                <Link to={'/'}>
                    <h2 className='font-[500] text-[#FFD840] text-[24px]'>KMPARK</h2>
                </Link>
                {
                    isPc ?
                        <div className='ml-auto'>
                            <ul className='flex space-x-8'>
                                <li className='li_hover'>
                                    장애접수
                                </li>
                                <li className='li_hover'>
                                    관리
                                </li>
                            </ul>
                        </div>
                        :
                        <div className='ml-auto'>
                            {
                                isOpen ?
                                    <div className='w-[45px] h-[40px] border bg_base rounded-md'>
                                        <button className='w-full h-full flex_center'
                                                onClick={(e: React.MouseEvent) => setIsOpen(!isOpen)}>
                                            {
                                                isOpen ?
                                                    <img src={Menu} alt={'menu'} width={40}/>
                                                    :
                                                    <img src={Menu} alt={'menu'} width={40}/>
                                            }
                                        </button>
                                    </div>
                                    :
                                    <div className='fixed top-0 right-0 w-[80px] h-full bg-black'>
                                        open
                                    </div>
                            }
                                </div>
                            }
                        </div>


                    </div>
                    )
                }
