import cn from "classnames";
import React, {useEffect, useState} from "react";
import {Location} from "../@types/common";
import {useMediaQuery} from "react-responsive";
import axiosInstance from "../api/axiosInstans";


export default function Management(){
    const [data, setData] = useState<Location[]>([]);
    const [parkingName, setParkinName] = useState('');
    const isPc = useMediaQuery({ query: "(min-width: 990px)" });
    const [scrolled, setScrolled] = useState(false);
    function isScrollable() {
        // 현재 문서의 높이
        const documentHeight = document.documentElement.scrollHeight;
        // 창의 높이
        const windowHeight = window.innerHeight;

        // 현재 문서의 높이가 창의 높이보다 크면 스크롤이 생겼음을 의미
        return documentHeight > windowHeight;
    }
    const onClickSearch = (e : React.MouseEvent) => {
        const fetchData = async () => {
            try {
                let data = {
                    parkingName : parkingName,
                    sort :'name'
                }
                const result: any = await axiosInstance.post('/parkingNameSerch',data);
                setData(result.data);
                if(result.length <= 0 ){
                    alert("데이터가 없습니다.")
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }

    useEffect(() => {
        setScrolled(isScrollable);
    }, [data]);
    return(
        <div className='w-full flex flex-col'>
            <div className='flex items-center w-full space-x-4 justify-center'>
                <div className='border w-[300px] h-[40px] flex items-center'>
                    <label className='basis-1/3 h-full flex_center bg-[#eeeeee]'>
                        현장
                    </label>
                    <input type={'text'} className='basis-2/3 h-full px-2' placeholder={'현장명을 입력해주세요.'}/>
                </div>
                <div className='w-[60px] h-[40px] flex_center border'>
                    <button className='w-full h-full bg_base text-white' onClick={onClickSearch(parkingName)}>
                        검색
                    </button>
                </div>
                <div className='w-[80px] h-[40px] flex_center border'>
                    <button className='w-full h-full bg_base text-white'>
                        현장추가
                    </button>
                </div>
            </div>
            <div className='w-full'>
                {data.length > 0 && (
                    <table className='w-full border'>
                        <thead>
                        <tr className='h-[40px] bg_base text-white'>
                            <th className='border-r'>코드</th>
                            <th className='border-r'>현장명</th>
                            <th className='border-r'>영업관할</th>
                        </tr>
                        </thead>
                        {data.map((location, index) => (
                            <tbody key={index} className='text-black'>
                            <tr className='text-center h-[60px] border-b'>
                                <td rowSpan={2} className='border-r'>{location.PJTcode}</td>
                                <td className='border-r'>{location.pklName}</td>
                                <td className='border-r'>{location.sales_region}</td>
                            </tr>
                            <tr className='h-[40px] border-b'>
                                <td colSpan={2} className='border-r pl-4'>
                                    {location.pklAddress}
                                </td>
                            </tr>
                            </tbody>
                        ))}
                    </table>
                )}
            </div>
        </div>
    )
}
