import {Location} from '../@types/common';
import cn from 'classnames';
import {useMediaQuery} from "react-responsive";
import React, {useState, useEffect} from "react";
import Modal from "react-modal";
import { useForm, SubmitHandler } from 'react-hook-form';
import Close from '../assets/images/btn_close_24px.svg';

interface Props {
    data: Location[]; // Location 타입의 배열을 받는 props
}
interface Param {
    type : string;
    name : string;
    cell : string;
    errorType : string;
    errorLocation : string;
    detail : string;
    carNum : string;
    choose : string;
    check01 : boolean;
    check02 : boolean;
}

export default function GetLocation({ data }: Props) {
    const isPc = useMediaQuery({ query: "(min-width: 990px)" });
    const [flag, setFlag] = useState(false);
    const [param, setParam] = useState<Location>();
    const {
        register,
        handleSubmit,
        control,
        formState: { errors },
    } = useForm<Param>({
        defaultValues: {
            type : '',
            name : '',
            cell : '',
            errorType : '',
            errorLocation : '',
            detail : '',
            carNum : '',
            choose : '',
            check01 : false,
            check02 : false,
        },
        mode: 'onChange',
    });
    const climRequest = (state : Location) => {
        setParam(state);
        setFlag(!flag);
    }
    useEffect(() => {
        // 모달이 열렸을 때 body에 overflow: hidden 스타일 적용
        if (flag) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = 'auto'; // 모달이 닫혔을 때 스타일 제거
        }
    }, [flag]);
    const onSubmit: SubmitHandler<Param> =  (data) => {
        console.log(data)
    };

    const onError = (errors: any) => {

       console.log(errors)
    };

    return (
        <div className={cn('w-full mt-8 flex_center', {'text-[10px]': !isPc, 'text-[14px]' : isPc})}>
            {data.length > 0 && (
                <table className='w-full border'>
                    <thead>
                    <tr className='h-[40px] bg_base text-white'>
                        <th className='border-r'>코드</th>
                        <th className='border-r'>현장명</th>
                        <th className='border-r'>영업관할</th>
                        <th>장애접수</th>
                    </tr>
                    </thead>
                    {data.map((location, index) => (
                        <tbody key={index} className='text-black'>
                        <tr className='text-center h-[60px] border-b'>
                            <td rowSpan={2} className='border-r'>{location.PJTcode}</td>
                            <td className='border-r'>{location.pklName}</td>
                            <td className='border-r'>{location.sales_region}</td>
                            <td rowSpan={2}><button className={cn(' border text-white bg-[#007bff] rounded-lg',{'h-[28px] w-[40px] text-[9px]' : !isPc, 'h-[40px] w-[80px]' : isPc})} onClick={(event : React.MouseEvent)=>climRequest(location)}>장애 접수</button></td>
                        </tr>
                        <tr className='h-[40px] border-b'>
                            <td colSpan={2} className='border-r pl-4'>
                                {location.pklAddress}
                            </td>
                        </tr>
                        </tbody>
                    ))}
                </table>
            )}
            <Modal isOpen={flag} style={!isPc ? mobi_customStyles : customStyles}>
                <div className='bg-white text-black font-bold h-[60px]'>
                    <div className='flex items-center h-full'>
                        <p className='pl-4'>{param?.pklName} 장애접수</p>
                        <button className='ml-auto pr-4' onClick={(e : React.MouseEvent) => setFlag(!flag)}><img src={Close} alt={'close'}/></button>
                    </div>
                </div>
                <div className='w-full flex_center flex-col base_font text-[14px]'>
                    <div className='w-full px-6 flex flex-col text-[12px] bg-[#eeeeee] pt-4'>
                        <p>({param?.PJTcode})</p>
                        <p>아래항목을 입력해주세요 <span className='text-[#f0821f]'>* 필수입력 항목</span></p>
                    </div>
                    <form className='w-full bg-[#eeeeee]' onSubmit={handleSubmit(onSubmit, onError)}>
                        <div className='px-6 my-4'>
                            <div className='w-full flex items-center h-[30px]'>
                                <label className='basis-1/2 flex items-center pl-8 h-[35px]'>
                                    <p>접수자 성명<span className='text-[#f0821f]'>&nbsp;*</span></p>
                                </label>
                                <div className='w-full basis-1/2 h-[35px] px-2'>
                                    <input className='w-full h-full px-2' placeholder={'성명을 입력해주세요.'}
                                           {...register('name', {
                                               required: true,
                                           })}
                                    />
                                </div>
                            </div>
                            <div className='w-full flex items-center h-[30px] mt-4'>
                                <label className='basis-1/2 flex items-center pl-8 h-[35px]'>
                                    <p>접수자 휴대전화<span className='text-[#f0821f]'>&nbsp;*</span></p>
                                </label>
                                <div className='w-full basis-1/2 h-[35px] px-2'>
                                    <input className='w-full h-full px-2' placeholder={'-없이 입력해주세요'}
                                           {...register('cell', {
                                               required: true,
                                           })}
                                    />
                                </div>
                            </div>
                            <div className='w-full flex items-center h-[30px] mt-4'>
                                <label className='basis-1/2 flex items-center pl-8 h-[35px]'>
                                    <p>장애구분<span className='text-[#f0821f]'>&nbsp;*</span></p>
                                </label>
                                <div className='w-full basis-1/2 h-[35px] px-2'>
                                    <select className='w-full h-full'
                                            {...register('errorType', {
                                                required: true,
                                            })}
                                    >
                                        <option selected disabled={true}>장애구분</option>
                                        <option value={'02'}>장애</option>
                                        <option value={'03'}>정기권</option>
                                        <option value={'04'}>환불</option>
                                    </select>
                                </div>
                            </div>
                            <div className='w-full flex items-center h-[30px] mt-4'>
                                <label className='basis-1/2 flex items-center pl-8 h-[35px]'>
                                    <p>장애발생위치<span className='text-[#f0821f]'>&nbsp;*</span></p>
                                </label>
                                <div className='w-full basis-1/2 h-[35px] px-2'>
                                    <select className='w-full h-full'
                                            {...register('errorLocation', {
                                                required: true,
                                            })}
                                    >
                                        <option disabled={true} selected>장애발생위치</option>
                                        <option value={'01'}>정산기출구</option>
                                        <option value={'02'}>정산기사전(상세내역에 기입)</option>
                                        <option value={'03'}>LPR입구</option>
                                        <option value={'04'}>LPR출구</option>
                                        <option value={'05'}>차단기입구</option>
                                        <option value={'06'}>차단기출구</option>
                                        <option value={'07'}>기타(상새내역에 기입)</option>
                                    </select>
                                </div>
                            </div>
                            <div className='w-full flex items-center h-[30px] mt-4'>
                                <label className='basis-1/2 flex items-center pl-8 h-[35px]'>
                                    <p>장애 상세 내용<span className='text-[#f0821f]'>&nbsp;*</span></p>
                                </label>
                                <div className='w-full basis-1/2 h-[35px] px-2'>
                                <textarea className='w-full px-2 h-[60px]' placeholder={'장애발생 내용'}
                                          {...register('detail', {
                                              required: true,
                                          })}
                                />
                                </div>
                            </div>
                            <div className='w-full flex items-center h-[30px] mt-12'>
                                <label className='basis-1/2 flex items-center pl-8 h-[35px]'>
                                    <p>차량번호<span className='text-[#f0821f]'>&nbsp;*</span></p>
                                </label>
                                <div className='w-full basis-1/2 h-[35px] px-2'>
                                    <input className='w-full h-full px-2' placeholder={'차량번호를 입력해주세요.'}
                                           {...register('carNum', {
                                               required: true,
                                           })}
                                    />
                                </div>
                            </div>
                            <div className='w-full flex items-center h-[30px] mt-4'>
                                <label className='basis-1/2 flex items-center pl-8 h-[35px]'>
                                    <p>현장조치사항</p>
                                </label>
                                <div className='w-full basis-1/2 h-[35px] px-2'>
                                    <select className='w-full h-full'
                                            {...register('choose', {
                                                required: false,
                                            })}
                                    >
                                        <option disabled={true} selected>현장조치사항</option>
                                        <option value={'01'}>수동 스위치 고정</option>
                                        <option value={'02'}>현장 업락 고정</option>
                                    </select>
                                </div>
                            </div>
                        </div>

                        <div className='bg-white'>
                            <div className='w-full flex flex-col py-4 base_font text-[14px] mt-4 px-4'>
                                <p className='text-[16px] font-bold text-black'>개인정보 수집 및 이용동의</p>
                                <div className='flex flex-col px-4 text-[12px]'>
                                    <ul className='list-decimal'>
                                        <li className='font-bold pt-2'>
                                            개인정보의 수집 및 이용목적
                                            <ol>
                                                <li className='font-thin'>장애발생 대응</li>
                                            </ol>
                                        </li>
                                        <li className='font-bold pt-2'>
                                            수집항목
                                            <ol>
                                                <li className='font-thin'>성명,연락처(휴대전화)</li>
                                            </ol>
                                        </li>
                                        <li className='font-bold pt-2'>
                                            개인정보의 이용 및 보유기간
                                            <ol>
                                                <li className='font-thin'>목적 완료 후 즉시 파기</li>
                                            </ol>
                                        </li>
                                    </ul>
                                </div>
                                <div className={cn({'text-[11px]': !isPc})}>
                                    <div className='flex_center w-full mt-4'>
                                        <div className='basis-1/6 flex'>
                                            <input type='checkbox' className='ml-auto mr-2'
                                                   {...register('check01', {
                                                       required: true,
                                                   })}
                                            />
                                        </div>
                                        <p className='basis-5/6'>위와 같이 개인정보수집 및 이용방침에 동의를 합니다.</p>
                                    </div>
                                    <div className='flex_center w-full mt-4'>
                                        <div className='basis-1/6 flex'>
                                            <input type='checkbox' className='ml-auto mr-2'
                                                   {...register('check02', {
                                                       required: true,
                                                   })}
                                            />
                                        </div>
                                        <p className='basis-5/6'>위와 같이 개인정보 제3자 제공에 동의합니다.</p>
                                    </div>
                                    <div className='flex_center w-full mt-4 '>
                                        <button className='button_on' type="submit">접수 완료</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </Modal>
        </div>
    );
}
