import React, { SetStateAction} from "react";
import Logo from '../assets/images/top_logo.png';
import cn from "classnames";
import { Link } from 'react-router-dom';
import {useMediaQuery} from "react-responsive";

interface Props {
    type?: string,
    comp?: string,
    setType : React.Dispatch<SetStateAction<string>>,
}
export default function Header() {
    const isPc = useMediaQuery({query : "(min-width : 990px"});

    console.log(isPc);
    return (
        <div className='w-full border-b h-[80px]'>
            <div className='header'>
                <Link to={'/'}>
                    <h2 className='font-[500] text-[#FFD840] text-[24px]'>KMPARK</h2>
                </Link>
                {
                    isPc ?
                        <div className='ml-auto'>
                            <ul className='flex space-x-8'>
                                <li className={cn('li_hover', { 'font-bold': activeSection === 'example' })} onClick={scrollToExample}>보험료 예시</li>
                                <li className={cn('li_hover', { 'font-bold': activeSection === 'info' })} onClick={scrollToInfo}>상품정보</li>
                                <li className={cn('li_hover', { 'font-bold': activeSection === 'limit' })} >담보 및 보상한도</li>
                                <li className={cn('li_hover', { 'font-bold': activeSection === 'qa' })} onClick={scrollToQa}>Q & A</li>
                            </ul>
                        </div>
                        :
                        <div>
                            fff
                        </div>
                }
            </div>


        </div>
    )
}
