import React, {SetStateAction, useEffect} from "react";
import Close from "../../assets/images/btn_close_24px.svg";
import cn from "classnames";
import Modal from "react-modal";
import {SubmitHandler, useForm} from "react-hook-form";
import {useMediaQuery} from "react-responsive";
import {modalStyles} from "../../utils/common";

interface Param {
    type : string;
    name : string;
    cell : string;
    errorType : string;
    errorLocation : string;
    detail : string;
    carNum : string;
    choose : string;
    check01 : boolean;
    check02 : boolean;
}
const mobi_customStyles = {
    overlay: {
        backgroundColor: "rgba(0,0,0,0.5)",
        zIndex : 100
    },
    content: {
        backgroundColor : "rgb(238,238,238)",
        left: "50%",
        margin: "auto",
        width: "360px",
        height: "calc(100vh - 120px)",
        padding: "0",
        overflow: "scroll",
        transform : "translate(-50%,0)"
    },
};
const customStyles = {
    overlay: {
        backgroundColor: "rgba(0,0,0,0.5)",
        zIndex : 100
    },
    content: {
        backgroundColor : "white",
        left: '0',
        margin: "auto",
        width: "460px",
        height: "850px",
        padding: "0",
        overflow: "hidden",
        //transform : "translate(-50%,0)"
    },
};

interface Props {
    type : string;
    PJTcode ?: string;
    pklName ?: string;
    flag : boolean;
    setFlag : React.Dispatch<SetStateAction<boolean>>;
}
export default function Modals({type, PJTcode, pklName, setFlag, flag} : Props) {
    const isPc = useMediaQuery({ query: "(min-width: 990px)" });
    const styles = modalStyles(type);
    const {
        register,
        handleSubmit,
        control,
        formState: { errors },
    } = useForm<Param>({
        defaultValues: {
            type : '',
            name : '',
            cell : '',
            errorType : '',
            errorLocation : '',
            detail : '',
            carNum : '',
            choose : '',
            check01 : false,
            check02 : false,
        },
        mode: 'onChange',
    });

    const onSubmit: SubmitHandler<Param> =  (data) => {
        data.type = type;
        console.log(data)
    };

    const onError = (errors: any) => {

        console.log(errors)
    };

    useEffect(() => {
        // 모달이 열렸을 때 body에 overflow: hidden 스타일 적용
        if (flag) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = 'auto'; // 모달이 닫혔을 때 스타일 제거
        }
    }, [flag]);

    return (
        <>
            {
                type === 'manage'
                    ?
                    <Modal isOpen={true} style={!isPc ? styles.mobi : styles.web}>
                        <div className='bg-white text-black font-bold h-[60px]'>
