import React, {SetStateAction, useState, useEffect} from 'react';
import axios from 'axios';
import { Document, Page, pdfjs  } from 'react-pdf';
import axiosInstans from "../api/axiosInstans";
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
type Props = {
    pdfUrl: string;
    setOpen : React.Dispatch<SetStateAction<boolean>>;
    type : string;
};


function PdfView ({setOpen, type } : Props)  {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [numPages, setNumPages] = useState<number | null>(null);
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [pageNumber, setPageNumber] = useState<number>(1);
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [pdfUrl, setPdfUrl] = useState('/get-pdf');


    const onDocumentLoadSuccess = ({ numPages } : any) => {
        setNumPages(numPages);
    };


    const changePage = (offset: number) => {
        setPageNumber(prevPageNumber => prevPageNumber + offset);
    };

    const previousPage = () => {
        changePage(-1);
    };

    const nextPage = () => {
        changePage(1);
    };
    const downloadPdf = () => {
        const pdfUrl = 'https://test-mycheckup-insu.simg.kr/download-pdf';
        // PDF 파일 다운로드 링크 생성
        const link = document.createElement('a');
        link.href = pdfUrl;
        link.setAttribute('download', '프로미고객사랑보험_보험약관.pdf'); // 다운로드할 파일 이름 설정
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    }

    useEffect(() => {
        const getPdfUrl = async () => {
            try {
                const response = await axios.get('/get-pdf', {
                    responseType: 'blob' // 데이터를 Blob 형식으로 받기
                });
                const pdfBlob = new Blob([response.data], { type: 'application/pdf' });
                setPdfUrl(URL.createObjectURL(pdfBlob)); // Blob URL로 PDF 설정
            } catch (error) {
                console.error('Error fetching PDF:', error);
            }
        };
        getPdfUrl();
    }, []);
    return (
        <div className='w-full h-full  flex_center flex-col bg-white'>
                <Document
                    file={pdfUrl}
                    onLoadSuccess={onDocumentLoadSuccess}
                >
                    <Page pageNumber={pageNumber}/>
                </Document>
            <div className='flex flex-col absolute bottom-0 bg-white w-full pb-4'>
                <div className='flex_center space-x-6'>
                    <button disabled={pageNumber <= 1} onClick={previousPage}>
                        이전 페이지
                    </button>
                    <p>Page {pageNumber} of {numPages}</p>
                    <button disabled={pageNumber >= (numPages || 1)} onClick={nextPage}>
                        다음 페이지
                    </button>
                </div>
                <div className='flex space-x-6 flex_center mt-4 text-white'>
                    <button className='button-color' onClick={(event : React.MouseEvent<HTMLButtonElement>) => setOpen(false)}>닫기</button>
                    <button className='button-color' onClick={downloadPdf}>저장</button>
                </div>
            </div>
        </div>
    );
};

export default PdfView;
