import React, { useState } from 'react';
import { Document, Page, pdfjs  } from 'react-pdf';
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
type PDFViewerProps = {
    path: string;
    scale: number;
};


export default function pdfView ({ pdfUrl } : any)  {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [numPages, setNumPages] = useState<number | null>(null);
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [pageNumber, setPageNumber] = useState<number>(1);

    const onDocumentLoadSuccess = ({ numPages } : any) => {
        setNumPages(numPages);
    };

    const onDocumentError = (error: Error) => {
        console.error("pdf viewer error", error);
    };

    const changePage = (offset: number) => {
        setPageNumber(prevPageNumber => prevPageNumber + offset);
    };

    const previousPage = () => {
        changePage(-1);
    };

    const nextPage = () => {
        changePage(1);
    };

    return (
        <div className='w-full h-full flex_center flex-col'>
            <div>
                <Document
                    file={'sample.pdf'}
                    onLoadSuccess={onDocumentLoadSuccess}
                >
                    <Page pageNumber={pageNumber}/>
                </Document>
                <div className='flex_center absolute bottom-0 w-full bg-white '>
                    <button disabled={pageNumber <= 1} onClick={previousPage}>
                        이전 페이지
                    </button>
                    <button disabled={pageNumber >= (numPages || 1)} onClick={nextPage}>
                        다음 페이지
                    </button>
                </div>
                <p>Page {pageNumber} of {numPages}</p>
            </div>
        </div>
    );
};
