import Logo from '../assets/images/logo2.png'
import React, {useState, useEffect} from 'react';
import Modals from '../component/modals';
import dayjs from 'dayjs';
import cn from 'classnames';

import {useMediaQuery} from "react-responsive";
export default function Home(){
    const isPc = useMediaQuery({ query: "(min-width: 990px)" });
    let today = dayjs().format('YYYY-MM-DD');
    let [open, setOpen] = useState(false);
    const [type, setType] = useState('');
    useEffect(() => {
        if (open) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = 'auto';
        }
    }, [open]);
    return (
        <div className='w-[calc(100%-20%)] pb-[40px]'>
            <div className='w-full flex_center'>
                <div>
                    <img src={Logo} alt={'logo'} width={isPc ? 100 : 60}/>
                </div>
                <div className={cn('font-bold',{'text-[14px] ml-4' : !isPc, 'text-[24px] ml-16' : isPc})}>
                    <h2>임베디드 보험의 선구자 SIMG</h2>
                </div>
            </div>
            <div className='w-full flex_center border mt-8 border-black flex-col py-16'>
                <div className={cn('text-blue-600 flex_center flex-col', {'ml-16' : isPc, 'ml-2' : !isPc})}>
                    <h2 className={cn('font-bold', {'text-[24px]': isPc, 'text-[14px] flex': !isPc})}>벨류맵 회원들을 위한 화재보험 {!isPc && <br/>}상담일정 입력</h2>
                    <p className={cn('mt-8 text-black', {'text-[12px]': !isPc, 'text-[20px]' : isPc})}>
                        님 상담이 가능하신 날짜 2개를 선택해주세요.
                    </p>
                    <p className={cn('text-black',{'text-[12px]': !isPc ,'text-[20px]' : isPc})}>
                        최대한 해당 일자에 맞춰 연락 드립니다.
                    </p>
                    <p className={cn('text-black', {'text-[12px]': !isPc , 'text-[20px]' : isPc})}>
                        상담일정 미 입력 시 임의의 날짜에 {!isPc && <br/>}상담 연락을 받아보실 수 있습니다.
                    </p>
                    <div className='border-[1px] w-[calc(100%-40%)] mt-4 border-black'></div>
                </div>
                <div className={cn('w-[calc(100%-20%)] flex_center my-4', {'text-[12px]' : !isPc})}>
                    <div className='basis-1/2 flex_center flex-col'>
                        <label>
                            상담가능일자(1차)
                        </label>
                        <input type={'date'} className={isPc ? 'input' : 'm_input'} min={today}/>
                    </div>
                    <div className='basis-1/2 flex_center flex-col'>
                        <label>
                            상담가능일자(2차)
                        </label>
                        <input type={'date'} className={isPc ? 'input' : 'm_input'} min={today}/>
                    </div>
                </div>
                <button className={isPc ? 'button' : 'm_button'}>상담일정 예약하기</button>
            </div>

            <div className='w-full flex_center border mt-8 border-black flex-col py-16'>
                <div className='flex_center flex-col'>
                    <h2 className={cn('font-bold text-blue-600',{'text-[16px]' : !isPc, 'text-[24px]' : isPc})}>벨류맵 회원들을 위한 {!isPc && <br/>}임대차보증금 소송 보험</h2>
                    <button className={isPc ? 'button bg-[#f4e5b2]' : 'm_button bg-[#f4e5b2]'} onClick={(event : React.MouseEvent<HTMLButtonElement>) => {setType('regi'); setOpen(true);}}>보험가입증명서 확인</button>
                    <button className={isPc ? 'button bg-[#c5dcfe]' : 'm_button bg-[#c5dcfe]'} onClick={(event : React.MouseEvent<HTMLButtonElement>) => {setType('pdf'); setOpen(true);}}>보험 약관 확인</button>
                    <button className={isPc ? 'button bg-[#cdfbcc]' : 'm_button bg-[#cdfbcc]'} onClick={(event : React.MouseEvent<HTMLButtonElement>) => {setType('process'); setOpen(true);}}>보험금 청구 절차</button>
                </div>
            </div>
            {open && <Modals open={open} setOpen={setOpen} type={type}/>}
        </div>
    )
}
