import React from 'react';
import styled from "styled-components";
import {useSelector} from "react-redux";

const SlideDiv = styled.div`
  & .wrap {
    position: relative;
    width: 400px;
    height: 570px;
    perspective: 800px;
    cursor: pointer;
  }

  & .wrap .card {
    width: 100%;
    height: 100%;
    transition: all 2s;
    transform-style: preserve-3d;
  }
  
  & .wrap:hover .card {
    transform: rotateY(180deg);
  }

  & .wrap .card > div {
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
    width: 100%;
    height: 100%;
    backface-visibility: hidden;
  }

  & .wrap .card .card-front {
    z-index: 10;
  }

  & .wrap .card .card-back {
    background-color: #ccc;
    z-index: 5;
    transform: rotateY(180deg);
  }

`
const Step2: React.FC = () => {
    const isMobile = useSelector((state : any) => state.mobile.isMobi);
    return (
        /*<SlideDiv>
            <div className="wrap">
                <div className="card">
                    <div className="card-front w-full">
                        <div className='center-flex'>
                            보험료
                        </div>
                    </div>
                    <div className="card-back">BACK</div>
                </div>
            </div>
        </SlideDiv>*/
        <div className="w-full h-full flex-col">
            <div className={isMobile ? 'mobile_step1' : 'web_step1'}>
                <div className="border-b-4 font-bold text-[16px] w-full pb-1 ">
                    가입 정보 입력
                </div>
            </div>
        </div>
    );
};

export default Step2;
