import { produce } from 'immer';

const USER_GOALS = 'useinfo/USER_GOALS';
const USER_DATE = 'useinfo/USER_DATE';
const USER_JUMIN = 'useinfo/USER_JUMIN';
const USER_TYPE = 'userinfo/USER_TYPE';
const USER_NAME = 'userinfo/USER_NAME';
const USER = 'userinfo/USER';
const ADD_USER = 'userinfo/ADD_USER';
const APL_AGREE = 'userinfo/APL_AGREE';
const EXIST_CSTMR = 'userinfo/EXIST_CSTMR';
const USER_AGREE = 'userinfo/USER_AGREE';
export const user = users => ({
  type : USER,
  users
})

export const existCstmr = exist => ({
  type : EXIST_CSTMR,
  exist
})

export const aplAgree = users => ({
  type : APL_AGREE,
  users
})
export const userGoals = users => ({
  type : USER_GOALS,
  users
});

export const userDate = users => ({
  type : USER_DATE,
  users
});

export const userJumin = users => ({
  type : USER_JUMIN,
  users
});

export const userType = users => ({
  type : USER_TYPE,
  users
});

export const addUser = users => ({
  type : ADD_USER,
  users
});

export const userName = users => ({
  type : USER_NAME,
  users
})

export const userAgree = users => ({
  type : USER_AGREE,
  users
})


const initialState = {
  exist : {
    Yn : false,
    reqPK : ''
  },
  users :{
    goal : '',
    strDate : '',
    endDate: '',
    birth : '',
    jumin : '',
    gender : '',
    product : '',
    name : '',
    phone : '',
    email : '',
    engName : '',
    aplAcc : '',
    amt : '',
    markAgree : [],
    add : []
  }
};

function userInfo(state = initialState, action) {
  switch (action.type){
    case  USER_GOALS :
      return produce(state, draft => {
        draft.users.goal = action.users.goal
      });
    case  USER_DATE :
      return produce(state, draft => {
        draft.users.strDate = action.users.strDate
        draft.users.endDate = action.users.endDate
      });
    case  USER_JUMIN :
      return produce(state, draft => {
        draft.users.birth = action.users.birth
        draft.users.gender = action.users.gender
        draft.users.add = []
      });
    case  USER_TYPE :
      return produce(state, draft => {
        draft.users.product = action.users.product
        draft.users.amt = action.users.amt
      });
    case  USER_NAME :
      return produce(state, draft => {
        draft.users.name = action.users.name
        draft.users.phone = action.users.phone
        draft.users.jumin = action.users.jumin
        draft.users.email = action.users.email
      });
    case USER_AGREE :
      return produce(state, draft => {
      draft.users.markAgree = action.users.markAgree
      })
    case USER :
      return produce(state, draft => {
        draft.users.name = action.users.name
        draft.users.phone = action.users.phone
        draft.users.jumin = action.users.jumin
        draft.users.email = action.users.email
        draft.users.engName = action.users.engName
      });
    case APL_AGREE :
      return produce(state, draft => {
        draft.users.aplAcc = action.users.aplAcc
      });
    case ADD_USER :
      return produce(state, draft => {
        draft.users.add = action.users.add
      })
    case EXIST_CSTMR :
      return produce(state, draft => {
        draft.exist.Yn = action.exist.Yn
        draft.exist.reqPK = action.exist.reqPK
      })
    default : return state;
  }
}
export default userInfo;
