import React, {useRef, useState} from 'react';
import styled from "styled-components";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import {useSelector} from "react-redux";
import {calculateInsAge, calDate} from "../utils/common";
import BottomNavigation from '@mui/material/BottomNavigation';
import BottomNavigationAction from '@mui/material/BottomNavigationAction';

const theme = createTheme({
    components: {
        MuiFormLabel: {
            styleOverrides: {
                root : {
                    fontSize : '13px',
                    top : '-4px !important'
                }
            },
        },
        MuiInputBase : {
            styleOverrides : {
                root : {
                    width : '160px',
                    height : '35px',
                    fontSize : '13px'
                }
            }
        }
    },
});
const Step2: React.FC = () => {
    const isMobile = useSelector((state : any) => state.mobile.isMobi);
    const state = useSelector((state : any) => state.user);
    const [value, setValue] = React.useState('high');

    const handleChange = (event: React.SyntheticEvent, newValue: string) => {
        setValue(newValue);
    };
    return (
        <div className="w-full h-full flex-col">
            <div className={isMobile ? 'mobile_step1' : 'flex items-center flex-col mt-4 h-full mx-6 relative'}>
                <div className="border-b-4 font-bold text-[16px] w-full pb-1 flex flex-col">
                    가입 정보 확인
                    <div className='center-flex w-full base_text pt-2'>
                        <h2 className='mr-auto'>
                            보험나이({calculateInsAge(state.birth)}세)
                        </h2>
                        <h2 className=''>
                            성별({state.gender === 'male' ? '남성' : '여성'})
                        </h2>
                        <h2 className='ml-auto'>
                            보험기간( {calDate(state.toDate,state.fromDate)}일)
                        </h2>
                    </div>
                </div>
                <div className="flex w-full border mt-2">
                    <ThemeProvider theme={theme}>
                    <BottomNavigation sx={{ width: 500 }} value={value} onChange={handleChange} showLabels >
                        <BottomNavigationAction
                            label="안심플랜"
                            value="normal"
                        />
                        <BottomNavigationAction
                            label="든든플랜"
                            value="high"
                        />
                    </BottomNavigation>
                    </ThemeProvider>
                </div>
                <div className={isMobile ? 'mobile_footer' : 'web_footer'}>
                    <button type="submit" className="w-full h-full">
                        보험 가입하기
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Step2;
