import React, { useState } from 'react';
import { useForm, SubmitHandler, useController } from 'react-hook-form';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import 'dayjs/locale/ko';
import InputDatePicker from '../components/inputDatePicker';
import SelectGender from '../components/selectGender';
import Time from '../components/time';
import { useDispatch } from 'react-redux';
import { setStep1 } from '../redux';
import { getAmt } from '../service/getPrimeum';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { ModalProps } from '../@types/tour';

interface Inputs {
    birth: string;
    genders: string;
    goal: string;
    strDate: string;
    endDate: string;
    strTime: string;
    endTime: string;
}

const theme = createTheme({
    components: {
        MuiTypography: {
            styleOverrides: {
                root: {
                    fontSize: '12px',
                },
            },
        },
    },
});

interface Step1Props extends ModalProps {
    setMsg: React.Dispatch<React.SetStateAction<string>>;
    isOpen: boolean;
    setIsOpen: React.Dispatch<React.SetStateAction<boolean>>;
}

const Step1: React.FC<Step1Props> = ({ setMsg, isOpen, setIsOpen }) => {
    const Regex = /\d{2}([0]\d|[1][0-2])([0][1-9]|[1-2]\d|[3][0-1])/g;
    const dispatch = useDispatch();
    const isMobile = /Mobi/i.test(window.navigator.userAgent);
    const [strDate, setStrDate] = useState<string>('');

    const {
        register,
        handleSubmit,
        control,
        formState: { errors },
    } = useForm<Inputs>({
        defaultValues: {
            birth: '',
            genders: '',
            goal: '',
            strDate: '',
            endDate: '',
            strTime: '',
            endTime: '',
        },
        mode: 'onChange',
    });

    // SelectGender 컴포넌트에서 가져온 field를 register에 추가
    const { field: genderField } = useController({ control, name: 'genders' });

    // birth에 대한 ref 추가
    const { field: birthField } = useController({ control, name: 'birth' });

    // goal에 대한 ref 추가
    const { field: goalField } = useController({ control, name: 'goal' });

    // strDate에 대한 ref 추가
    const { field: strDateField } = useController({ control, name: 'strDate' });

    // endDate에 대한 ref 추가
    const { field: endDateField } = useController({ control, name: 'endDate' });

    const onSubmit: SubmitHandler<Inputs> = async (data) => {
        try {
            dispatch(
                setStep1({
                    gender: data.genders,
                    fromDate: `${data.strDate} ${data.strTime}`,
                    toDate: `${data.endDate} ${data.endTime}`,
                    birth: data.birth,
                    goal: data.goal,
                })
            );

            const response = await getAmt({
                gender: data.genders,
                pdtCode: 'hnsb1002',
                birth: data.birth,
                fromDate: `${data.strDate} ${data.strTime}`,
                toDate: `${data.endDate} ${data.endTime}`,
            });

            console.log(response);
        } catch (error) {
            console.error(error);
        }
    };

    const onError = (errors: any) => {
        setIsOpen(true);
        setMsg('');

        // 여기서 에러를 출력해봅니다.
        console.log(errors);
    };

    return (
        <div className="w-full h-full flex-col">
            <div className={isMobile ? 'mobile_step1' : 'web_step1'}>
                <div className="border-b-4 font-bold text-[16px] w-full pb-1 ">
                    가입 정보 입력
                </div>
                <form
                    onSubmit={handleSubmit(onSubmit, onError)}
                    className="h-full w-full flex-col mt-4 relative"
                >
                    {/* 여행 목적 선택 */}
                    <div>
                        {/* ... (이전 코드) */}
                    </div>
                    {/* 여행 기간 선택 */}
                    <div className="font-bold mt-4 text-[#0B0273]">여행 기간</div>
                    <div className="flex w-full mt-4">
                        <div className="w-full">
                            {/* InputDatePicker에 대한 ref 추가 */}
                            <InputDatePicker control={control} strDate={strDate} setStrDate={setStrDate} />
                            <div className="flex w-full">
                                <div className="mr-auto">
                                    <Time name={'strTime'} />
                                </div>
                                <div className="ml-auto">
                                    <Time name={'endTime'} />
                                </div>
                            </div>
                            <div className="flex-col flex">
                                {(errors.strDate || errors.endDate || errors.strTime || errors.endTime) ? (
                                    <span className="base_text text-red-500 mt-2">여행 일정을 선택해주세요</span>
                                ) : (
                                    <span className="base_text mt-2">* 출발 전까지 가입 가능합니다.</span>
                                )}
                            </div>
                        </div>
                    </div>
                    <div className="mt-4 flex">
                        <div className="flex-col w-full">
                            <h2 className="font-bold pb-2 text-[#0B0273]">여행자 정보</h2>
                            <div className="flex flex-col">
                                <span className="text-[12px] pb-2">생년월일</span>
                                <div className="flex w-full">
                                    <div className="flex-col ml-auto basis-1/2">
                                        {/* birthField를 통해 birth를 등록 */}
                                        <input
                                            placeholder={'숫자6자리 ex)990101'}
                                            {...register('birth', {
                                                required: true,
                                                pattern: { value: Regex, message: '생년월일을 확인해주세요.' },
                                            })}
                                            maxLength={6}
                                            className="input w-full"
                                        />
                                        <div className="base_text mt-2">* 만 79세까지 가입이 가능합니다.</div>
                                        {errors.birth && (
                                            <div className="base_text text-red-500">생년월일을 입력해주세요</div>
                                        )}
                                    </div>
                                    <div className="flex-col center-flex basis-1/2 mr-auto">
                                        {/* genderField를 통해 genders를 등록 */}
                                        <SelectGender control={control} />
                                        {errors.genders && (
                                            <span className="base_text text-red-500 mt-2 ml-4">성별을 선택해주세요</span>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Step1;
