import { useController } from "react-hook-form";
import React, { useEffect } from "react";

function SelectGender({ control }: any) {
    // useController를 바깥으로 빼놓지 않도록 수정
    const { field } = useController({ control, name: 'genders' });

    // control이 변경될 때마다 실행되는 useEffect
    useEffect(() => {
        // 여기에서 필요한 로직을 수행
        // 예를 들어, 컨트롤에 대한 설정이 필요한 경우 이곳에서 설정

        // return cleanup 함수 (옵션)
        return () => {
            // 이 부분에서 정리(clean-up) 로직을 추가할 수 있습니다.
            // 예: 이벤트 리스너 해제 등
        };
    }, [control]); // control이 변경될 때만 useEffect가 실행

    return (
        <div className='w-[100px] h-[30px] border ml-6 rounded-xl bg-[#D9D9D9] '>
            <div className="w-full flex items-center justify-center h-full text-[13px] font-semibold font-['Hack-Bold']">
                {/* 나머지 코드는 이전과 동일 */}
                <button
                    className={field.value === 'male' ? 'genderBtnOn' : 'genderBtnOff'}
                    onClick={() => field.onChange('male')}
                >
                    남자
                </button>
                <button
                    className={field.value === 'female' ? 'genderBtnOn' : 'genderBtnOff'}
                    onClick={() => field.onChange('female')}
                >
                    여자
                </button>
            </div>
        </div>
    );
}

export default SelectGender;
