import React, {useRef, useState} from 'react';
import { Tabs, TabsRef } from 'flowbite-react';
import styled from "styled-components";
import {useSelector} from "react-redux";
import {calculateInsAge, calDate} from "../utils/common";

const SlideDiv = styled.div`
  & .wrap {
    position: relative;
    width: 400px;
    height: 570px;
    perspective: 800px;
    cursor: pointer;
  }

  & .wrap .card {
    width: 100%;
    height: 100%;
    transition: all 2s;
    transform-style: preserve-3d;
  }
  
  & .wrap:hover .card {
    transform: rotateY(180deg);
  }

  & .wrap .card > div {
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
    width: 100%;
    height: 100%;
    backface-visibility: hidden;
  }

  & .wrap .card .card-front {
    z-index: 10;
  }

  & .wrap .card .card-back {
    background-color: #ccc;
    z-index: 5;
    transform: rotateY(180deg);
  }

`
const Step2: React.FC = () => {
    const tabsRef = useRef<TabsRef>(null);
    const [activeTab, setActiveTab] = useState(0);
    const isMobile = useSelector((state : any) => state.mobile.isMobi);
    const state = useSelector((state : any) => state.user);
    console.log(state);
    return (
        /*<SlideDiv>
            <div className="wrap">
                <div className="card">
                    <div className="card-front w-full">
                        <div className='center-flex'>
                            보험료
                        </div>
                    </div>
                    <div className="card-back">BACK</div>
                </div>
            </div>
        </SlideDiv>*/
        <div className="w-full h-full flex-col">
            <div className={isMobile ? 'mobile_step1' : 'web_step1'}>
                <div className="border-b-4 font-bold text-[16px] w-full pb-1 flex flex-col">
                    가입 정보 확인
                    <div className='center-flex w-full base_text pt-2'>
                        <h2 className='mr-auto'>
                            보험나이({calculateInsAge(state.birth)}세)
                        </h2>
                        <h2 className=''>
                            성별({state.gender === 'male' ? '남성' : '여성'})
                        </h2>
                        <h2 className='ml-auto'>
                            보험기간( {calDate(state.toDate,state.fromDate)}일)
                        </h2>
                    </div>
                </div>
                <div className="flex flex-col gap-3">
                    <Tabs aria-label="Default tabs" style="default" ref={tabsRef} onActiveTabChange={(tab) => setActiveTab(tab)} >
                        <Tabs.Item active title="든든플랜">
                            ㄻㄴㄹ
                        </Tabs.Item>
                        <Tabs.Item title="안심플랜">
                            This is <span className="font-medium text-gray-800 dark:text-white">Dashboard tab's associated content</span>.
                            Clicking another tab will toggle the visibility of this one for the next. The tab JavaScript swaps classes to
                            control the content visibility and styling.
                        </Tabs.Item>
                    </Tabs>
                </div>
            </div>
        </div>
    );
};

export default Step2;
