import React from "react";
import { DesktopTimePicker } from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import 'dayjs/locale/ko';
import dayjs, {Dayjs} from "dayjs";
import { Controller } from "react-hook-form";
import styled from "styled-components";

const CustomDate = styled(DesktopTimePicker )`
  & .MuiOutlinedInput-root {
    width: 160px;
    height: 45px;
  }

  & .css-1d3z3hw-MuiOutlinedInput-notchedOutline {
    top : 0;
  }
`
function InputTimePicker({ control, id } : any ) {
    return (
        <div className='flex'>
            <div>
                <Controller
                    control={control}
                    name='strTime'
                    // render를 사용해서, field값을 복사하거나 꺼내 쓰면 된다.
                    // field안에는 value나 onBlur와 같은 함수도 있음
                    // render안의 onChange를 조작해, onChange안에 들어갈 값을
                    // 선택할 수 있다.
                    render={({ field: { onChange } }) => (
                        // antd의 datepicker에서 e.target.value는
                        // moment 객체 그대로를 반환하기에,
                        // "2021-04-15"와 같은 값을 얻고싶다면, 두번째 파라미터
                        // "dateString"을 추가해서 값을 넣어야 한다.
                        <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="ko">
                            <CustomDate
                                slotProps={{
                                    toolbar: {
                                        // Customize value display
                                        //toolbarFormat: 'YYYY',
                                        hidden: true,
                                    },
                                    actionBar: {
                                        actions: ['cancel','accept'],
                                    }
                                }}
                                ampm={false}
                                timeSteps={{ minutes: 30 }}
                                format="HH:mm"
                                onChange={(newDate) =>
                                    // @ts-ignore
                                    onChange(dayjs(newDate).format('HH:mm'))
                                }
                                label="출발 시간"
                            />
                        </LocalizationProvider>
                    )}
                />
            </div>
            <div className='ml-4'>
                <Controller
                    control={control}
                    name='endTime'
                    // render를 사용해서, field값을 복사하거나 꺼내 쓰면 된다.
                    // field안에는 value나 onBlur와 같은 함수도 있음
                    // render안의 onChange를 조작해, onChange안에 들어갈 값을
                    // 선택할 수 있다.
                    render={({ field: { onChange } }) => (
                        // antd의 datepicker에서 e.target.value는
                        // moment 객체 그대로를 반환하기에,
                        // "2021-04-15"와 같은 값을 얻고싶다면, 두번째 파라미터
                        // "dateString"을 추가해서 값을 넣어야 한다.
                        <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="ko">
                            <CustomDate
                                slotProps={{
                                    toolbar: {
                                        // Customize value display
                                        //toolbarFormat: 'YYYY',
                                        hidden: true,
                                    },
                                    actionBar: {
                                        actions: ['cancel','accept'],
                                    }
                                }}
                                ampm={false}
                                timeSteps={{ minutes: 30 }}
                                format="HH:mm"
                                onChange={(newDate) =>
                                    // @ts-ignore
                                    onChange(dayjs(newDate).format('HH:mm'))
                                }
                                label="출발 시간"
                            />
                        </LocalizationProvider>
                    )}
                />
            </div>
        </div>
    );
};

export default InputTimePicker;
