import React, {useEffect, useRef, useState} from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import { Mousewheel } from 'swiper/modules';
import {cls} from "../hooks/utils";
import ClockIcon from '../asset/images/clock-regular.svg';
import XIcon from '../asset/images/xmark-solid.svg';
import {Controller} from "react-hook-form";
function Time({ control, id } : any ) {
    const TIME_HOURS =
        [
            100,130,200,230,300,330,400,430,500,530,600,630,700,730,800,830,900,930,1000,1030,1100,1130,1200,1230,1300,1330,1400,1430,1500,1530,1600,1630,1700,1730,
            1800,1830,1900,1930,2000,2030,2100,2130,2200,2230,2300,2330,2400,2430
        ];
    const [hours, setHours] = useState(0);
    const [open, setOpen] = useState(false);
    const [time, setTime] = useState('');
    const inputRef = useRef<HTMLInputElement>(null);
    const onClickHandler = (hour : string) => {
        let temp = hour.padStart(4,'0');
        temp = temp.substring(0,2) + ":" + temp.substring(2,4);
        setTime(temp);
        if(inputRef.current){
            inputRef.current.setAttribute('value', temp);
            inputRef.current.dispatchEvent(new Event('change', { bubbles: true }));
        }
        setOpen(false);
    }
    return (
        <div className='flex mt-4'>
                <div>
                    <Controller
                        control={control}
                        name='strTime'
                        rules={{ required: true}}
                        // render를 사용해서, field값을 복사하거나 꺼내 쓰면 된다.
                        // field안에는 value나 onBlur와 같은 함수도 있음
                        // render안의 onChange를 조작해, onChange안에 들어갈 값을
                        // 선택할 수 있다.
                        render={({ field: { onChange } }) => (
                            // antd의 datepicker에서 e.target.value는
                            // moment 객체 그대로를 반환하기에,
                            // "2021-04-15"와 같은 값을 얻고싶다면, 두번째 파라미터
                            // "dateString"을 추가해서 값을 넣어야 한다.
                            <div className='w-[160px] relative border rounded-lg'>
                                <div className='flex items-center py-2 px-2  text-[13px]'>
                                    <input className='w-[110px]' type={'text'} ref={inputRef} placeholder={'출발 시간'} onChange={(event :React.ChangeEvent<HTMLInputElement>) => onChange(event)} readOnly={true} />
                                    <button className='ml-[10px]' onClick={(e:React.MouseEvent<HTMLElement>)=>setOpen(!open)}>{!open ? <img src={ClockIcon} width={'18px'} alt={'clock'}/> : <img className='ml-1' src={XIcon} width={'14px'} alt={'xmark'}/>}</button>
                                </div>
                                {
                                    open &&
                                    <div className='border w-[200px] shadow-xl rounded-xl absolute top-[40px] bg-white z-30'>
                                        <div className='text-[12px] font-semibold  flex items-center justify-center pt-2'>
                                            도착시간
                                        </div>
                                        <Swiper
                                            direction={'vertical'}
                                            /*initialSlide={10}*/
                                            slidesPerView={3}
                                            loop={true}
                                            mousewheel
                                            modules={[Mousewheel]}
                                            loopAdditionalSlides={5}
                                            slideToClickedSlide
                                            centeredSlides
                                            className="h-[160px] px-[1.1rem]"
                                            onSlideChange={(swiper : any) => {setHours(TIME_HOURS[swiper.realIndex])}}
                                        >
                                            {TIME_HOURS.map((hour : number) => (
                                                <SwiperSlide key={hour}>
                                                    <div
                                                        className={cls("flex h-[40px] cursor-pointer items-center justify-center",
                                                            hours === hour
                                                                ? "H2 text-primary-700 font-bold border-b border-t base_color border-b-[#0075FF] border-t-[#0075FF]"
                                                                : "text-[15px]  font-medium"
                                                        )}
                                                    >
                                                        {(hour+"").padStart(4,"0").substring(0,2)+' : '+(hour+"").padStart(4,"0").substring(2,4)}
                                                    </div>
                                                </SwiperSlide>
                                            ))}
                                        </Swiper>
                                        <div className='w-full flex text-[12px] font-semibold'>
                                            <div className='basis-1/2 border-black/40 border text-center mx-2 my-2 rounded-lg py-1.5'>
                                                <button className='w-full h-full' onClick={(event : React.MouseEvent<HTMLElement>) => setOpen(false)}>
                                                    취소
                                                </button>
                                            </div>
                                            <div className='basis-1/2 border  text-center mx-2 my-2 rounded-lg py-1.5 base_bg text-white'>
                                                <button className='w-full h-full' onClick={(e:React.MouseEvent<HTMLElement>)=> onClickHandler(hours.toString())}>
                                                    저장
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                }
                            </div>
                        )}
                    />
                </div>
                <div>
                    <Controller
                        control={control}
                        name='endTime'
                        rules={{ required: true}}
                        // render를 사용해서, field값을 복사하거나 꺼내 쓰면 된다.
                        // field안에는 value나 onBlur와 같은 함수도 있음
                        // render안의 onChange를 조작해, onChange안에 들어갈 값을
                        // 선택할 수 있다.
                        render={({ field: { onChange } }) => (
                            // antd의 datepicker에서 e.target.value는
                            // moment 객체 그대로를 반환하기에,
                            // "2021-04-15"와 같은 값을 얻고싶다면, 두번째 파라미터
                            // "dateString"을 추가해서 값을 넣어야 한다.
                            <div className='w-[160px] relative border rounded-lg'>
                                <div className='flex items-center py-2 px-2  text-[13px]'>
                                    <input className='w-[110px]' type={'text'} ref={inputRef} placeholder={'출발 시간'} onChange={(event :React.ChangeEvent<HTMLInputElement>) => onChange(event)} readOnly={true} />
                                    <button className='ml-[10px]' onClick={(e:React.MouseEvent<HTMLElement>)=>setOpen(!open)}>{!open ? <img src={ClockIcon} width={'18px'} alt={'clock'}/> : <img className='ml-1' src={XIcon} width={'14px'} alt={'xmark'}/>}</button>
                                </div>
                                {
                                    open &&
                                    <div className='border w-[200px] shadow-xl rounded-xl absolute top-[40px] bg-white z-30'>
                                        <div className='text-[12px] font-semibold  flex items-center justify-center pt-2'>
                                            도착시간
                                        </div>
                                        <Swiper
                                            direction={'vertical'}
                                            /*initialSlide={10}*/
                                            slidesPerView={3}
                                            loop={true}
                                            mousewheel
                                            modules={[Mousewheel]}
                                            loopAdditionalSlides={5}
                                            slideToClickedSlide
                                            centeredSlides
                                            className="h-[160px] px-[1.1rem]"
                                            onSlideChange={(swiper : any) => {setHours(TIME_HOURS[swiper.realIndex])}}
                                        >
                                            {TIME_HOURS.map((hour : number) => (
                                                <SwiperSlide key={hour}>
                                                    <div
                                                        className={cls("flex h-[40px] cursor-pointer items-center justify-center",
                                                            hours === hour
                                                                ? "H2 text-primary-700 font-bold border-b border-t base_color border-b-[#0075FF] border-t-[#0075FF]"
                                                                : "text-[15px]  font-medium"
                                                        )}
                                                    >
                                                        {(hour+"").padStart(4,"0").substring(0,2)+' : '+(hour+"").padStart(4,"0").substring(2,4)}
                                                    </div>
                                                </SwiperSlide>
                                            ))}
                                        </Swiper>
                                        <div className='w-full flex text-[12px] font-semibold'>
                                            <div className='basis-1/2 border-black/40 border text-center mx-2 my-2 rounded-lg py-1.5'>
                                                <button className='w-full h-full' onClick={(event : React.MouseEvent<HTMLElement>) => setOpen(false)}>
                                                    취소
                                                </button>
                                            </div>
                                            <div className='basis-1/2 border  text-center mx-2 my-2 rounded-lg py-1.5 base_bg text-white'>
                                                <button className='w-full h-full' onClick={(e:React.MouseEvent<HTMLElement>)=> onClickHandler(hours.toString())}>
                                                    저장
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                }
                            </div>
                        )}
                    />
                </div>
        </div>
    );
};

export default Time;
