import React from 'react';
import { useForm, SubmitHandler } from "react-hook-form"
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import { createTheme, Theme, ThemeProvider  } from '@mui/material/styles';
import Checkbox from '@mui/material/Checkbox';
import { green, purple } from '@mui/material/colors';

type Inputs = {
    example: string
    exampleRequired: string,
    gender : string,
    goal : string
}

const outerTheme  = createTheme({
    palette: {
        primary: {
            main: purple[500],
        },
        secondary: {
            main: green[500],
        },
    },
});
const Step1: React.FC = () => {
    const gender = ["남자", "여자"];
    const {
        register,
        handleSubmit,
        watch,
        formState: { errors },
    } = useForm<Inputs>()
    const onSubmit: SubmitHandler<Inputs> = (data) => console.log(data)
    return (
        <div className='center-flex flex-col mx-4'>
            <div className='border-b-4 font-bold text-[16px] w-full pb-1'>
                가입 정보 입력
            </div>
            <div className='w-full flex flex-col mt-4'>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className='font-bold'>여행 목적</div>
                    <ThemeProvider theme={outerTheme}>
                        <RadioGroup
                            row
                            aria-labelledby="demo-row-radio-buttons-group-label"
                            name="row-radio-buttons-group"
                        >
                            <FormControlLabel value="여행/관광" control={<Radio size="small" {...register("goal")}/>} label="여행/관광" />
                            <FormControlLabel value="출장/업무" control={<Radio size="small" {...register("goal")}/>} label="출장/업무" />
                            <FormControlLabel value="유학/연수/캠프" control={<Radio size="small" {...register("goal")} />} label="유학/연수/캠프" />
                        </RadioGroup>
                    </ThemeProvider>
                    <ThemeProvider theme={outerTheme}>
                        <Checkbox defaultChecked color="secondary" />
                        <ThemeProvider
                            theme={(theme: Theme) =>
                                createTheme({
                                    ...theme,
                                    palette: {
                                        ...theme.palette,
                                        primary: {
                                            main: green[500],
                                        },
                                    },
                                })
                            }
                        >
                            <Checkbox defaultChecked />
                            <Checkbox defaultChecked color="secondary" />
                        </ThemeProvider>
                    </ThemeProvider>
                    {/* register your input into the hook by invoking the "register" function */}
                    <input defaultValue="test" {...register("example")} />
                    {/* include validation with required or other standard HTML validation rules */}
                    <input {...register("exampleRequired", { required: true })} />
                    {/* errors will return when field validation fails  */}
                    {errors.exampleRequired && <span>This field is required</span>}

                    <input type="submit" />
                </form>
            </div>
        </div>
    );
};

export default Step1;
