        const [isConnected, setConnected] = useState(false);

        // SSH 연결 상태를 확인하는 함수 (실제 동작에 맞게 수정 필요)
        const checkSshConnection = async () => {
            // 실제로는 SSH 연결 상태를 확인하는 로직이 들어갈 것입니다.
            // 예를 들면 API 호출이나 WebSocket을 이용한 통신 등이 있을 수 있습니다.

            // 임시로 isConnected 상태를 토글하는 예시
            setConnected((prev) => !prev);
        };

        return (
            <div>
                {/* 상태 표시 컴포넌트 사용 */}
                <SshStatus isConnected={isConnected} />

                {/* SSH 연결 확인 버튼 */}
                <button onClick={checkSshConnection}>Check SSH Connection</button>
            </div>
        );
    };

export default SSHMonitor;
