import { Controller } from "react-hook-form";
import React, {useState} from "react";

function SelectGender({control} : any) {
    const [gender, setGender] = useState('');
    return (
        <Controller
            control={control}
            name='gender'
            rules={{ required: true}}
            // render를 사용해서, field값을 복사하거나 꺼내 쓰면 된다.
            // field안에는 value나 onBlur와 같은 함수도 있음
            // render안의 onChange를 조작해, onChange안에 들어갈 값을
            // 선택할 수 있다.
            render={({ field: { onChange } }) => (
        <div className='w-[100px] h-[30px] border ml-6 rounded-xl bg-[#D9D9D9] '>
            <div className="w-full flex items-center justify-center h-full text-[13px] font-semibold font-['Hack-Bold']">
                <button className={gender === 'male' ? 'genderBtnOn' : 'genderBtnOff'} onClick={(event : React.MouseEvent<HTMLElement>) => {
                    setGender('male');
                    onChange('male');
                }}>
                    남자
                </button>
                <button className={gender === 'female' ? 'genderBtnOn' : 'genderBtnOff'} onClick={(event : React.MouseEvent<HTMLElement>) => {
                    setGender('female');
                    onChange('male');
                }}>
                    여자
                </button>
            </div>
        </div>
            )}
        />
    )
}
export default SelectGender;
