import React, {useRef, useState} from 'react';
import styled from "styled-components";
import {useSelector} from "react-redux";
import {calculateInsAge, calDate} from "../utils/common";

const SlideDiv = styled.div`
  & .wrap {
    position: relative;
    width: 400px;
    height: 570px;
    perspective: 800px;
    cursor: pointer;
  }

  & .wrap .card {
    width: 100%;
    height: 100%;
    transition: all 2s;
    transform-style: preserve-3d;
  }
  
  & .wrap:hover .card {
    transform: rotateY(180deg);
  }

  & .wrap .card > div {
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
    width: 100%;
    height: 100%;
    backface-visibility: hidden;
  }

  & .wrap .card .card-front {
    z-index: 10;
  }

  & .wrap .card .card-back {
    background-color: #ccc;
    z-index: 5;
    transform: rotateY(180deg);
  }

`
const Step2: React.FC = () => {
    const isMobile = useSelector((state : any) => state.mobile.isMobi);
    const state = useSelector((state : any) => state.user);
    console.log(state);
    return (
        /*<SlideDiv>
            <div className="wrap">
                <div className="card">
                    <div className="card-front w-full">
                        <div className='center-flex'>
                            보험료
                        </div>
                    </div>
                    <div className="card-back">BACK</div>
                </div>
            </div>
        </SlideDiv>*/
        <div className="w-full h-full flex-col">
            <div className={isMobile ? 'mobile_step1' : 'flex items-center flex-col mt-4 h-full mx-6 relative'}>
                <div className="border-b-4 font-bold text-[16px] w-full pb-1 flex flex-col">
                    가입 정보 확인
                    <div className='center-flex w-full base_text pt-2'>
                        <h2 className='mr-auto'>
                            보험나이({calculateInsAge(state.birth)}세)
                        </h2>
                        <h2 className=''>
                            성별({state.gender === 'male' ? '남성' : '여성'})
                        </h2>
                        <h2 className='ml-auto'>
                            보험기간( {calDate(state.toDate,state.fromDate)}일)
                        </h2>
                    </div>
                </div>
                <div className="flex w-full">
                    <BottomNavigation sx={{ width: 500 }} value={value} onChange={handleChange}>
                        <BottomNavigationAction
                            label="Recents"
                            value="recents"
                            icon={<RestoreIcon />}
                        />
                        <BottomNavigationAction
                            label="Favorites"
                            value="favorites"
                            icon={<FavoriteIcon />}
                        />
                        <BottomNavigationAction
                            label="Nearby"
                            value="nearby"
                            icon={<LocationOnIcon />}
                        />
                        <BottomNavigationAction label="Folder" value="folder" icon={<FolderIcon />} />
                    </BottomNavigation>
                </div>
                <div className={isMobile ? 'mobile_footer' : 'web_footer'}>
                    <button type="submit" className="w-full h-full">
                        보험 가입하기
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Step2;
