import React from "react";
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import 'dayjs/locale/ko';
import dayjs, {Dayjs} from "dayjs";
import { Controller } from "react-hook-form";
import styled from "styled-components";

// control을 받아오고,

const CustomDate = styled(DatePicker)`
    & .MuiOutlinedInput-root {
      width: 160px;
      height: 45px;
    }
  
    & .css-1d3z3hw-MuiOutlinedInput-notchedOutline {
      top : 0;
    }
`

function InputDatePicker({ control, id } : any ) {
    const dateFormat = "YYYY-MM-DD";

    return (
        // Controller를 선언한 후 control을 속성으로 넣어주면 된다.
        <Controller
            control={control}
            name={id}
            // render를 사용해서, field값을 복사하거나 꺼내 쓰면 된다.
            // field안에는 value나 onBlur와 같은 함수도 있음
            // render안의 onChange를 조작해, onChange안에 들어갈 값을
            // 선택할 수 있다.
            render={({ field: { onChange } }) => (
                // antd의 datepicker에서 e.target.value는
                // moment 객체 그대로를 반환하기에,
                // "2021-04-15"와 같은 값을 얻고싶다면, 두번째 파라미터
                // "dateString"을 추가해서 값을 넣어야 한다.
                <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="ko">
                    <div className='flex'>
                        <div>
                            <CustomDate
                                slotProps={{
                                    toolbar: {
                                        // Customize value display
                                        //toolbarFormat: 'YYYY',
                                        hidden: true,
                                    },
                                    actionBar: {
                                        actions: ['cancel', 'accept'],
                                    }
                                }}
                                onChange={(newDate) =>
                                    // @ts-ignore
                                    onChange(dayjs(newDate).format('YYYY-MM-DD'))
                                }
                                label="집에서 출발 일시"
                                format={'YYYY-MM-DD'}
                                shouldDisableDate={day => {
                                    return dayjs(dayjs(day as Dayjs).format(`YYYY-MM-DD`)).isBefore(
                                        dayjs().subtract(1, 'day')
                                    );
                                }}
                            />
                        </div>
                        <div className='mr-8'>
                            <CustomDate
                                slotProps={{
                                    toolbar: {
                                        // Customize value display
                                        //toolbarFormat: 'YYYY',
                                        hidden: true,
                                    },
                                    actionBar: {
                                        actions: ['cancel', 'accept'],
                                    }
                                }}
                                onChange={(newDate) =>
                                    // @ts-ignore
                                    onChange(dayjs(newDate).format('YYYY-MM-DD'))
                                }
                                label="집에 도착 일시"
                                format={'YYYY-MM-DD'}
                                shouldDisableDate={day => {
                                    return dayjs(dayjs(day as Dayjs).format(`YYYY-MM-DD`)).isBefore(
                                        dayjs()
                                    );
                                }}
                            />
                        </div>
                    </div>
                </LocalizationProvider>
            )}
        />
    );
}

export default InputDatePicker;
