import React, {useState} from 'react';
import { useForm, SubmitHandler } from "react-hook-form"
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import styled from "styled-components";
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import 'dayjs/locale/ko';
import dayjs, {Dayjs} from "dayjs";

type Inputs = {
    example: string
    exampleRequired: string,
    gender : string,
    goal : string,
    strDate : string,
    endDate : string
}

const CustomRadio  = styled(FormControlLabel)`
  & .css-ahj2mt-MuiTypography-root {
    font-size: 14px;
    font-weight: 550;
  }
`
const CustomDate = styled(DatePicker)`
    & .MuiFormControl-root {
      
    }
`

const Step1: React.FC = () => {
    const [start, setStart] = useState<string | null>('');
    const [end, setEnd] = useState<string>('');
    const today = dayjs().subtract(1, 'day')
    const {
        register,
        handleSubmit,
        watch,
        formState: { errors },
    } = useForm<Inputs>()
    const onSubmit: SubmitHandler<Inputs> = (data) => console.log(data)
    return (
        <div className='center-flex flex-col mx-4'>
            <div className='border-b-4 font-bold text-[16px] w-full pb-1'>
                가입 정보 입력
            </div>
                <form onSubmit={handleSubmit(onSubmit)} className='h-full w-full flex flex-col mt-4'>
                    {/*여행 목적 선택*/}
                    <div>
                        <div className='font-bold'>여행 목적</div>
                        <RadioGroup
                            row
                            aria-labelledby="demo-row-radio-buttons-group-label"
                            name="row-radio-buttons-group"
                        >
                            <CustomRadio value="여행/관광" control={<Radio size="small" {...register("goal")}/>} label="여행/관광" />
                            <CustomRadio value="출장/업무" control={<Radio size="small" {...register("goal")}/>} label="출장/업무" />
                            <CustomRadio value="유학/연수/캠프" control={<Radio size="small" {...register("goal")} />} label="유학/연수/캠프" />
                        </RadioGroup>
                        <h2 className='text-[12px]'>* 여행국가에 대한 입국제한 조치 상태를 꼭 <a href={'www.naver.com'} target={'_blank'} rel={'noreferrer'} className='base_color'>확인</a>하시기 바랍니다.</h2>
                    </div>

                    {/*여행 기간 선택*/}
                    <div className='mt-4'>
                        <div className='font-bold mb-2'>여행 기간</div>
                        <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="ko">
                            <DatePicker
                                slotProps={{
                                    toolbar: {
                                        // Customize value display
                                        //toolbarFormat: 'YYYY',
                                        hidden: true,
                                    },
                                    actionBar: {
                                        actions: ['cancel','accept'],
                                    }
                                }}
                                label="집에서 출발 일시"
                                format={'YYYY-MM-DD'}
                                shouldDisableDate={day => {
                                    return dayjs(dayjs(day as Dayjs).format(`YYYY-MM-DD`)).isBefore(
                                        today
                                    );
                                }}
                            />
                        </LocalizationProvider>
                    </div>

                    <input {...register("exampleRequired", { required: true })} />
                    {/* errors will return when field validation fails  */}
                    {errors.exampleRequired && <span>This field is required</span>}
                    <input type="submit" />
                </form>
            </div>
    );
};

export default Step1;
