import React from 'react';
import {useInput} from "../hooks/useInput";
import { useForm, SubmitHandler } from "react-hook-form"


type Inputs = {
    example: string
    exampleRequired: string
}

const Step1: React.FC = () => {
    const [ name, onChangeName ] = useInput("");
    const {
        register,
        handleSubmit,
        watch,
        formState: { errors },
    } = useForm<Inputs>()
    const onSubmit: SubmitHandler<Inputs> = (data) => console.log(data)
    return (
        <div className='center-flex flex-col mx-4'>
            <div className='border-b-4 font-bold text-[16px] w-full pb-1'>
                가입 정보 입력
            </div>
            <div className='w-full flex flex-col mt-4'>
                <form onSubmit={onSubmit}>
                    <input type="text" name="firstName" ref={register({ required: true })} />
                    {errors.firstName && <span>First name is required.</span>}

                    <input type="text" name="lastName" ref={register({ required: true })} />
                    {errors.lastName && <span>Last name is required.</span>}
                    <button>전송</button>
                </form>
            </div>
        </div>
    );
};

export default Step1;
