import React from 'react';
import styled from "styled-components";

const SlideDiv = styled.div`
  & .wrap {
    position: relative;
    width: 200px;
    height: 400px;
    margin: 50px auto;
    border-radius: 20px;
    perspective: 500px;
    cursor: pointer;
  }

  & .wrap .card {
    width: 100%;
    height: 100%;
    transition: all 2s;
    transform-style: preserve-3d;
  }
  
  & .wrap:hover .card {
    transform: rotateY(180deg);
  }

  & .wrap .card > div {
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    font-size: 20px;
    border-radius: 20px;
    backface-visibility: hidden;
  }

  & .wrap .card .card-front {
    z-index: 10;
  }

  & .wrap .card .card-back {
    background-color: #ccc;
    z-index: 5;
    transform: rotateY(180deg);
  }

`
const Step2: React.FC = () => {
    return (
        <SlideDiv>
            <div className="wrap">
                <div className="card">
                    <div className="card-front w-full bg-black">FRONT</div>
                    <div className="card-back">BACK</div>
                </div>
            </div>
        </SlideDiv>
    );
};

export default Step2;
