import React, {useState} from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import { Mousewheel, Pagination } from 'swiper/modules';
import {cls} from "../hooks/utils";
import ClockIcon from '../asset/images/clock-regular.svg';
const Time: React.FC = () => {
    const TIME_HOURS = Array.from(Array(24).keys());
    const [hours, setHours] = useState(0);
    const [open, setOpen] = useState(false);
    const TIME_MINUTES = [10,30];

    return (
        <div className='w-full flex justify-center items-center h-screen'>
            <div className='w-[200px] relative border rounded-lg '>
                <div className='flex items-center py-2 px-2'>
                    <button className='ml-auto' onClick={(e:React.MouseEvent<HTMLElement>)=>setOpen(!open)}>{hours === 0 ? <img src={ClockIcon} width={'20px'} alt={'clock'}/> : '닫기'}</button>
                </div>
                {
                    open &&
                    <div className='border w-[200px] shadow-xl rounded-xl absolute top-8'>
                        <div className='text-[12px] font-semibold  flex items-center justify-center pt-2'>
                            도착시간
                        </div>
                        <Swiper
                            direction={'vertical'}
                            slidesPerView={3}
                            mousewheel
                            modules={[Mousewheel]}
                            loopAdditionalSlides={5}
                            slideToClickedSlide
                            centeredSlides
                            className="h-[160px] px-[1.1rem]"
                            onSlideChange={(swiper : any) => setHours(swiper.realIndex)}
                        >
                            {TIME_HOURS.map((hour : number) => (
                                <SwiperSlide key={hour}>
                                    <div
                                        className={cls("flex h-[40px] cursor-pointer items-center justify-center",
                                            hours === hour
                                                ? "H2 text-primary-700 font-bold border-b border-t base_color border-b-[#0075FF] border-t-[#0075FF]"
                                                : "text-[15px]  font-medium"
                                        )}
                                    >
                                        {(hour + "").padStart(2, "0") +" : "+'30'}
                                    </div>
                                </SwiperSlide>
                            ))}
                        </Swiper>
                        <div className='w-full flex text-[12px] font-semibold'>
                            <div className='basis-1/2 border-black/40 border text-center mx-2 my-2 rounded-lg py-1.5'>
                                <button className='w-full h-full'>
                                    취소
                                </button>
                            </div>
                            <div className='basis-1/2 border  text-center mx-2 my-2 rounded-lg py-1.5 base_bg text-white'>
                                <button className='w-full h-full'>
                                    저장
                                </button>
                            </div>
                        </div>
                    </div>
                }
            </div>
        </div>
    );
};

export default Time;
