import React, {useState} from 'react';
import { useForm, SubmitHandler } from "react-hook-form"
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import { styled } from '@mui/system';
import 'dayjs/locale/ko';
import InputDatePicker from "../components/inputDatePicker";
import SelectGender from "../components/selectGender";
import Time from "../pages/time";
import {useDispatch} from "react-redux";
import {setStep1} from "../redux";
import {getAmt} from "../service/getPrimeum";
import { createTheme, ThemeProvider } from "@mui/material/styles";

type Inputs = {
    birth: string,
    gender : string,
    goal : string,
    strDate : string,
    endDate : string,
    strTime : string,
    endTime : string
}

const theme = createTheme({
    components: {
        MuiTypography: {
            styleOverrides: {
                root : {
                    fontSize : '13px',
                }
            },
        },
    },
});


const Step1: React.FC = () => {
    const dispatch = useDispatch();
    const isMobile = (/Mobi/i.test(window.navigator.userAgent));
    const [strDate, setStrDate] = useState('');
    const {
        register,
        handleSubmit,
        control,
        formState: { errors },
    } = useForm<Inputs>()
    const onSubmit: SubmitHandler<Inputs> = (data) => {
       dispatch(setStep1({gender : data.gender, fromDate : data.strDate, toDate : data.endDate, birth : data.birth, goal : data.goal}));

        getAmt({gender : data.gender, pdtCode : 'hnsb1002', birth : data.birth, fromDate : data.strDate+" "+data.strTime, toDate : data.endDate+" "+data.endTime})
            .then(res => console.log(res))
            .catch(error => console.log(error));
    }
    return (
        <div className={isMobile ? 'mobile_step1' : 'web_step1'}>
            <div className='border-b-4 font-bold text-[16px] w-full pb-1 '>
                가입 정보 입력
            </div>
                <form onSubmit={handleSubmit(onSubmit)} className='h-full w-full flex-col mt-4 relative'>
                    {/*여행 목적 선택*/}
                    <div>
                        <div>
                            <div className='font-bold text-[#0B0273]'>여행 목적</div>
                            <ThemeProvider theme={theme}>
                            <RadioGroup
                                row
                                aria-labelledby="demo-row-radio-buttons-group-label"
                                name="row-radio-buttons-group"
                            >
                                <FormControlLabel value="여행/관광" control={<Radio size="small" {...register("goal", { required: true })}/>} label="여행/관광" />
                                <FormControlLabel value="출장/업무" control={<Radio size="small" {...register("goal", { required: true })}/>} label="출장/업무" />
                                <FormControlLabel value="유학/연수/캠프" control={<Radio size="small" {...register("goal", { required: true })} />} label="유학/연수/캠프" />
                            </RadioGroup>
                            </ThemeProvider>
                            {errors.goal && <span className='base_text text-red-500'>여행목적을 선택해주세요</span>}
                            <h2 className='base_text'>* 여행국가에 대한 입국제한 조치 상태를 꼭 <a href={'www.naver.com'} target={'_blank'} rel={'noreferrer'} className='base_color'>확인</a>하시기 바랍니다.</h2>
                        </div>

                        {/*여행 기간 선택*/}
                        <div className='font-bold mt-4 text-[#0B0273]'>여행 기간</div>
                        <div className='flex w-full mt-4'>
                            <div className='mr-auto'>
                                <InputDatePicker
                                    // control을 자식 컴포넌트에 넘겨주기
                                    control={control}
                                    strDate={strDate}
                                    setStrDate={setStrDate}
                                />
                                <div className='flex'>
                                    <div className='mr-4'>
                                        <Time control={control} id={'strTime'}/>
                                    </div>
                                    <Time control={control} id={'endTime'}/>
                                </div>
                                {/*<InputTimePicker
                                    control={control}
                                />*/}
                                <div className='flex-col flex'>
                                    {(errors.strDate || errors.endDate || errors.strTime || errors.endTime) ?
                                        <span className='base_text text-red-500 mt-2'>여행 일정을 선택해주세요</span>
                                        :
                                        <span className='base_text mt-2'>* 출발전까지 가입가능 합니다.</span>
                                    }
                                </div>
                            </div>
                        </div>
                        <div className='mt-4 flex'>
                            <div className='flex-col'>
                                <h2 className='font-bold pb-2 text-[#0B0273]'>여행자 정보</h2>
                                <div className='flex flex-col'>
                                    <span className='text-[12px] pb-2'>생년월일</span>
                                    <div className='flex'>
                                        <div className='flex-col'>
                                            <input placeholder={'숫자8자리 ex)19990101'} {...register("birth", { required: true })} maxLength={6} className='input' />
                                            <div className='base_text mt-2'>* 만 79세까지 가입이 가능합니다.</div>
                                            {errors.birth && <div className='base_text text-red-500'>생년월일을 입력해주세요</div>}
                                        </div>
                                        <div className='flex-col items-center justify-center flex'>
                                            <SelectGender
                                                control={control}
                                            />
                                            {errors.gender && <span className='base_text text-red-500 mt-2 ml-4'>성별을 선택해주세요</span>}
                                        </div>
                                    </div>
                                </div>
                            </div>
                            {/* errors will return when field validation fails  */}
                        </div>
                    </div>
                    <div className={isMobile ? 'mobile_footer' : 'web_footer'}>
                        <button type="submit" className='w-full h-full'>보험료 확인하기</button>
                    </div>
                </form>
            </div>
    );
};

export default Step1;
