import React from "react";
import { DesktopTimePicker } from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import 'dayjs/locale/ko';
import { Swiper, SwiperSlide } from "swiper/react";
import dayjs, {Dayjs} from "dayjs";
import { Controller } from "react-hook-form";
import styled from "styled-components";

const CustomDate = styled(DesktopTimePicker )`
  & .MuiOutlinedInput-root {
    width: 160px;
    height: 40px;
    font-size: 13px;
  }
  & .MuiList-root {
   background-color: black; 
  }
  & .css-1yq5fb3-MuiButtonBase-root-MuiIconButton-root {
    padding-bottom : 1px;
  }
  & .css-14s5rfu-MuiFormLabel-root-MuiInputLabel-root{
    font-size: 12px;
  }
  & .css-1d3z3hw-MuiOutlinedInput-notchedOutline {
    top : 0;
  }
  & .MuiPaper-root {
    background-color: red;
  }
`
function InputTimePicker({ control, id } : any ) {
    let defaultTime = dayjs().set('hours',10).set('minutes',30);
    const TIME_HOURS = Array.from(Array(24).keys());
    const TIME_MINUTES = Array(12)
        .fill(0)
        .map((num, i) => i * 5);
    return (
        <div className='flex mt-4'>
            <div>
                <Controller
                    control={control}
                    name='strTime'
                    rules={{ required: true}}
                    // render를 사용해서, field값을 복사하거나 꺼내 쓰면 된다.
                    // field안에는 value나 onBlur와 같은 함수도 있음
                    // render안의 onChange를 조작해, onChange안에 들어갈 값을
                    // 선택할 수 있다.
                    render={({ field: { onChange } }) => (
                        // antd의 datepicker에서 e.target.value는
                        // moment 객체 그대로를 반환하기에,
                        // "2021-04-15"와 같은 값을 얻고싶다면, 두번째 파라미터
                        // "dateString"을 추가해서 값을 넣어야 한다.
                        /*<LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="ko">
                            <CustomDate
                                slotProps={{
                                    toolbar: {
                                        // Customize value display
                                        //toolbarFormat: 'YYYY',
                                        hidden: true,
                                    },
                                    actionBar: {
                                        actions: ['cancel','accept'],
                                    },
                                    field: {
                                        readOnly: true
                                    }
                                }}
                                defaultValue={defaultTime}
                                ampm={false}
                                timeSteps={{ minutes: 30 }}
                                format="HH:mm"
                                onChange={(newDate) =>
                                    // @ts-ignore
                                    onChange(dayjs(newDate).format('HH:mm'))
                                }
                                label="출발 시간"
                            />
                        </LocalizationProvider>*/
                        <Swiper
                            className="h-[16.8rem]  px-[1.1rem]"
                            direction={"vertical"}
                            slidesPerView={3}
                            mousewheel
                            loopAdditionalSlides={5}
                            slideToClickedSlide
                            centeredSlides
                            onSlideChange={(swiper : any) => console.log(swiper)}
                        >
                            {TIME_HOURS.map((hour) => (
                                <SwiperSlide key={hour}>
                                        {(hour + "").padStart(2, "0")}
                                </SwiperSlide>
                            ))}
                        </Swiper>
                    )}
                />
            </div>
            <div className='ml-4'>
                <Controller
                    rules={{ required: true}}
                    control={control}
                    name='endTime'
                    // render를 사용해서, field값을 복사하거나 꺼내 쓰면 된다.
                    // field안에는 value나 onBlur와 같은 함수도 있음
                    // render안의 onChange를 조작해, onChange안에 들어갈 값을
                    // 선택할 수 있다.
                    render={({ field: { onChange } }) => (
                        // antd의 datepicker에서 e.target.value는
                        // moment 객체 그대로를 반환하기에,
                        // "2021-04-15"와 같은 값을 얻고싶다면, 두번째 파라미터
                        // "dateString"을 추가해서 값을 넣어야 한다.
                        <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="ko">
                            <CustomDate
                                slotProps={{
                                    toolbar: {
                                        // Customize value display
                                        //toolbarFormat: 'YYYY',
                                        hidden: true,
                                    },
                                    actionBar: {
                                        actions: ['cancel','accept'],
                                    },
                                    field: {
                                        readOnly: true
                                    }
                                }}
                                ampm={false}
                                defaultValue={defaultTime}
                                timeSteps={{ minutes: 30 }}
                                format="HH:mm"
                                onChange={(newDate) =>
                                    // @ts-ignore
                                    onChange(dayjs(newDate).format('HH:mm'))
                                }
                                label="도착 시간"
                            />
                        </LocalizationProvider>
                    )}
                />
            </div>
        </div>
    );
};

export default InputTimePicker;
