import React, {useEffect, useState} from "react";

export type InfoProps = {
    bpk : string,
    cName : string,
    cCell : string,
    cJumin : string,
    cMail : string,
    cPost : string,
    cAddr1 : string,
    cAddr2 : string,
    cJobN : string,
    cCarCc : string,
    cUseN : string,
    cJobLocal : string,
    cMoney : string,
    cDrink : string,
    cWeekD : string,
    cOneD : string,
    cSmoke : string,
    cWeekS : string,
    cOneS : string,
    cHeight : string,
    cWeight : string,
    cBank : string,
    cAccount : string,
    cPayDt : string,
}
const CarNumInput = (props : {info : InfoProps, setInfo : React.Dispatch<React.SetStateAction<InfoProps>>}) => {
    const [carNum, setCarNum] = useState<string>('');
    useEffect(() => {
        props.setInfo((prevState) => {
            return { ...prevState, cCarCc: carNum }
        });
    }, [carNum]);
    return (
        <div className='flex-col pb-8 px-2'>
            <div className='flex items-center'>
                <div className='sub_title pb-1 pl-2 pr-2'>
                    차량번호
                </div>
                <input type={'text'} onChange={(e:React.ChangeEvent<HTMLInputElement>) => setCarNum(e.target.value)} className='input' placeholder='차량번호를 입력해주세요'/>
                <div className='sub_title pb-1 pl-2'>
                    차량번호
                </div>
                <input type={'text'} onChange={(e:React.ChangeEvent<HTMLInputElement>) => setCarNum(e.target.value)} className='input' placeholder='차량번호를 입력해주세요'/>
            </div>
        </div>
    )
}
export default CarNumInput;
