import React, {useState} from "react";
import { produce } from "immer";

const NameInput = ({setInfo} : any) => {
    const [name , setName] = useState<string>('');
    const [check, setCheck] = useState<boolean>(false);
    const onChangeHandler = (e : React.ChangeEvent<HTMLInputElement>) => {
        const {value} = e.target;
        const nameRegex = /^[ㄱ-ㅎ가-힣a-zA-Z]+$/;
        setName(value);
        if(nameRegex.test(value)){
            setCheck(true);
             setInfo(produce((state, draft) => {
                draft.name = name;
            }));
        }else {
            setCheck(false);
        }
    }
    return(
        <div className='pt-8 flex flex-col pb-8'>
            <div className='sub_title pb-1 pl-2'>
                이름
            </div>
            <input type={'text'} onChange={(e)=>onChangeHandler(e)} className='input'/>
            {!check && name !== '' && <h2 className='text-red-500 text-[11px] pt-2'>이름을 확인해주세요.</h2>}
        </div>
    )
}
export default NameInput;
