import { produce } from 'immer';

const USER_PHONE = 'useinfo/USER_PHONE';

export const userPhone = (phone : string) => ({
    type : USER_PHONE,
    phone
});


const initialState = {
    phone : ''
};

function userInfo(state = initialState, action: { type: any; phone: string; })  {
    switch (action.type){
        case  USER_PHONE :
            return produce(state, draft => {
                draft.phone = action.phone
            });
        default : return state;
    }
}
export default userInfo;
