import React, {useEffect, useState} from "react";

type InfoProps = {
    bpk : string,
    cName : string,
    cCell : string,
    cJumin : string,
    cMail : string,
    cPost : string,
    cAddr1 : string,
    cAddr2 : string,
    cJob : string,
    carNm : string,
    carUse : string,
}
const JuminInput = (props : {info : InfoProps, setInfo : React.Dispatch<React.SetStateAction<InfoProps>>}  ) => {
    const [jumin , setJumin] = useState<string>('');
    const [check, setCheck] = useState<boolean>(false);
    const onChangeHandler = (e : React.ChangeEvent<HTMLInputElement>) => {
        const { value  } = e.target;
        const nameRegex = /^[ㄱ-ㅎ가-힣a-zA-Z]+$/;
        setJumin(value);
        if(nameRegex.test(jumin)){
            setCheck(true);
        }else {
            setCheck(false);
        }
    }
    useEffect(() => {
        if(check){
            props.setInfo((prevState) => {
                return { ...prevState, cJumin: jumin }
            });
        }
    }, [jumin]);
    return(
        <div className='flex flex-col pb-8'>
            <div className='sub_title pb-1 pl-2'>
                주민번호
            </div>
            <div className='flex justify-space'>
                <input type={'text'} onChange={onChangeHandler} className='input basis-1/3' placeholder='앞자리'/>
                <input type={'text'} onChange={onChangeHandler} className='input input basis-1/3' placeholder='뒷자리'/>
            </div>
            {!check && jumin !== '' && <h2 className='text-red-500 text-[11px] pt-2'>주민번호를 확인해주세요.</h2>}
        </div>
    )
}
export default JuminInput;
