import { produce } from 'immer';

const USER_PHONE = 'userinfo/USER_PHONE';

export const userPhone = (info: { phone: string }) => ({
    type : USER_PHONE,
    info
});

type PhoneType = {
    phone : string
}

const initialState : PhoneType = {
    info : {
        cell : ''
    }
};

function userInfo(state = initialState, action: { type: any; phone: string; })  {
    switch (action.type){
        case  USER_PHONE :
            return produce(state, draft => {
                draft.phone = action.phone
            });
        default : return state;
    }
}
export default userInfo;
