import React, {Dispatch, SetStateAction, useState} from "react";
import {InfoProps} from "./inputs/carNumInput";

interface Props {
    setCell: React.Dispatch<React.SetStateAction<string>>;
    setIsCell : React.Dispatch<React.SetStateAction<boolean>>;
}
const PhoneInput = (props : {info : InfoProps, setInfo : React.Dispatch<React.SetStateAction<InfoProps>>} ) => {
    const [phone, setPhone] = useState<string>('');
    const [isCheck, setIsCheck] = useState<boolean>(false);
    const onChangeHandler = (e : React.ChangeEvent<HTMLInputElement>) => {
        const phoneRegex =/^\d{3}\d{3,4}\d{4}$/;
        const { value } = e.target;
        const onlyNumber = value.replace(/[^-0-9]/g, '');
        setPhone(onlyNumber);
        if(phoneRegex.test(onlyNumber)){
            setIsCheck(true);
            props.setInfo((prevState) => {
                return {
                    ...prevState,
                    cCell : onlyNumber
                }
            });
        }else {
            setIsCheck(false);
            props.setInfo((prevState) => {
                return {
                    ...prevState,
                    cCell : ''
                }
            });
        }
    }
    return (
        <div className='flex w-full pb-4 items-center justify-center'>
            <div className='sub_title ml-8 mb-6'>
                연락처
            </div>
            <div className='flex-col ml-12'>
                <input placeholder='휴대폰 번호를 입력해주세요' className='input'  value={phone} type='string' onChange={(e) => onChangeHandler(e)}/>
                <h2 className='text-gray-500/50 font-semibold text-[12px] mt-2'>(-)없이 입력해주세요.</h2>
                {!isCheck && phone !== '' && <h2 className='text-red-500 text-[12px]'>휴대폰 번호 형식이 맞지 않습니다.</h2>}
            </div>
        </div>
    )
}
export default PhoneInput;
