import React, {useState} from "react";
type InfoProps = {
    bpk : string,
    cName : string,
    cCell : string,
    cJumin : string,
    cMail : string,
    cPost : string,
    cAddr1 : string,
    cAddr2 : string,
    cJob : string,
    carNm : string,
    carUse : string,
}
const JobInput =(props : {info : InfoProps, setInfo : React.Dispatch<React.SetStateAction<InfoProps>>}) => {
    const [job, setJob] = useState<string>('');
    const [check, setCheck] = useState<boolean>(false);
    return(
        <div className='flex flex-col pb-8'>
            <div className='sub_title pb-1 pl-2'>
                직업
            </div>
            <div className='flex justify-between mx-2'>
                <input type={'text'}  maxLength={7} value={job} onChange={(e : React.ChangeEvent<HTMLInputElement>) => setJob(e.target.value)} className='input basis-1/3' placeholder='직업을 입력해주세요'/>
            </div>
            {!check && job !== '' && <h2 className='text-red-500 text-[11px] pt-2'>주민번호를 확인해주세요.</h2>}
        </div>
    )
}
export default JobInput;
