import {useNavigate} from "react-router-dom";
import React, {useEffect, useState} from "react";
import {sendJoin} from "../../pages/api/sendJoin";
import Modal from "react-modal";
type InfoProps = {
    bpk : string,
    cName : string,
    cCell : string,
    cJumin : string,
    cMail : string,
    cPost : string,
    cAddr1 : string,
    cAddr2 : string,
    cJob : string,
    carNm : string,
    carUse : string,
}
const RegisterButton = (props : {info : InfoProps} ) => {
    const page = useNavigate();
    const [flag, setFlag] = useState<boolean>(false);
    const customStyles = {
        overlay: {
            backgroundColor: "rgba(0,0,0,0.5)",
        },
        content: {
            left: "50%",
            margin: "auto",
            width: "350px",
            height: "350px",
            padding: "0",
            overflow: "hidden",
            transform : "translate('-50%',0)"
        },
    };
    const onClickHandler = (e : React.MouseEvent<HTMLElement>) => {
         sendJoin(props.info)
             .then((res : any) => {
                 if(res.status === 200){
                     setFlag(true);
                 }
        }).catch((error) => {
            alert(error.msg);
        })
    }
    useEffect(() => {
        console.log(flag)
    }, [flag]);
    return (
        <div className='w-full pb-[170px]'>
            <div className='border mx-8 my-8 py-[10px] px-4 rounded-lg bg-[#32a087] text-white font-bold text-[14px] flex items-center justify-center'>
                <button className='w-full' onClick={(e) => onClickHandler(e)}>신청하기</button>
            </div>
            {   flag &&
                    <Modal isOpen={flag} style={customStyles}>
                        <div>

                        </div>
                    </Modal>
            }
        </div>
    )
}
export default RegisterButton;
