import { produce } from 'immer';

const USER_PHONE = 'userinfo/USER_PHONE';
export const GLOBAL_LOADING = "LOADING";
export const GLOBAL_LOADED = "LOADED";

export const userPhone = (  cell: string ) => ({
    type : USER_PHONE,
    cell
});


type PhoneType = {
    cell : string
    loading : boolean
}

const initialState : PhoneType = {
    cell : '',
    loading : false
};

function userInfo(state = initialState, action: { type: any; cell : string, loading : boolean })  {
    switch (action.type){
        case  USER_PHONE :
            return produce(state, draft => {
                draft.cell = action.cell
            });
        case GLOBAL_LOADING :
            return produce(state, draft => {
                draft.loading = true
            });
        case GLOBAL_LOADED :
            return produce(state, draft => {
                draft.loading = false
            });
        default : return state;
    }
}
export default userInfo;
