import DaumPostcode from 'react-daum-postcode';
import React, {useState} from "react";
const PostCode = () => {
    const [flag, setFlag] = useState<boolean>(false);
    const [cPost, setCpost] = useState<string>('');
    const [cAddr1, setCaddr1] = useState<string>('');
    const [cAddr2, setCaddr2] = useState<string>('');

    const handleComplete = (data : any) => {
        let fullAddress :string = data.address;
        let extraAddress = '';
        console.log(data)
        if (data.addressType === 'R') {
            if (data.bname !== '') {
                extraAddress += data.bname;
            }
            if (data.buildingName !== '') {
                extraAddress += (extraAddress !== '' ? `, ${data.buildingName}` : data.buildingName);
            }
            fullAddress += (extraAddress !== '' ? ` (${extraAddress})` : '');
            setCpost(data.zonecode);
            setCaddr1(data.roadAddress);
        }
        setFlag(false)
    }
    return (
        <div className='flex flex-col w-[500px]'>
            <div className='flex justify-between base_text'>
                <div className='border basis-1/4 flex justify-center py-2 rounded-lg border-[#32A086]'>{cPost}</div>
                <button onClick={(e:React.MouseEvent<HTMLElement>)=>setFlag(!flag)} className='basis-1/4 border py-2 rounded-lg border-[#32A086]'>우편번호 검색</button>
            </div>
            <div className='border mt-4 flex justify-center py-2 rounded-lg border-[#32A086]'>{cAddr1}</div>
            <div className='border mt-4 flex justify-center py-2 rounded-lg border-[#32A086]'>{cAddr2}</div>
            { flag &&
                <div>
                    <DaumPostcode onComplete={handleComplete}/>
                </div>
            }
        </div>
    )
}
export default PostCode;
