import React, {useEffect, useState} from "react";
import { sendMessage } from "../../pages/api/sendMessage";
import { randomCode } from "../util/common";
type sendParam = {
    code : string,
    cell : string
}
interface CellProps {
    cell : string,
    setCode : React.Dispatch<React.SetStateAction<string>>,
    isCell : boolean
}
const SendButton = ({cell, setCode, isCell} : CellProps) => {
    const [sendFlag, setSendFlag] = useState<boolean>(false);
    const onClickHandler = (e : React.MouseEvent<HTMLElement>) => {
         sendMessage(cell)
            .then((res : any) => {
                setCode(res.code);
            }).catch((error) => {
                alert(error.msg);
         })
    }
    return (
        <div>
            <button className={isCell ? 'button_on' : 'button_off'} onClick={onClickHandler} disabled={!isCell}>인증번호 전송</button>
        </div>
    )
}
export default SendButton;
