import React, {useState} from "react";
const PhoneInput = () => {
    const [phone, setPhone] = useState<String>('');
    const [right, setRight] = useState<boolean>(false);
    const onChangeHandler = (e : React.ChangeEvent<HTMLInputElement>) => {
        const phoneRegex =/^\d{3}\d{3,4}\d{4}$/;
        setPhone(e.target.value);
        if(phoneRegex.test(e.target.value)){
            setRight(true);
        }else {
            setRight(false);
        }
    }
    return (
        <div>
            <input placeholder='휴대폰 번호를 입력해주세요' className='input'  value={phone} type='text' onChange={(e) => onChangeHandler(e)}/>
            <h2 className='text-gray-500/50 font-semibold text-[12px] pt-1'>(-)없이 입력해주세요.</h2>
            {!right && phone !== '' && <h2 className='text-red-500 text-[12px]'>휴대폰 번호 형식이 맞지 않습니다.</h2>}
        </div>
    )
}
export default PhoneInput;
